  // CLEANUP ON UNMOUNT
  // =========================================================================

  useEffect(() => {
    return () => {
      stopRequesterPolling();
      stopApproverPolling();
      clearEphemeralKey();
    };
  }, [stopRequesterPolling, stopApproverPolling, clearEphemeralKey]);

  return {
    // Requester (new device)
    requestApproval,
    cancelRequest,
    approvalStatus,
    approvalError,

    // Approver (existing device)
    pollPendingRequests,
    stopApproverPolling,
    approveRequest,
    denyRequest,
    pendingRequests,
    isPolling: isPollingPending,
  };
}

