  3 *
  4 * Creates IPNS records locally using @cipherbox/crypto and publishes
  5 * via the backend API relay to delegated-ipfs.dev.
  6 */
  7
  8import {
  9  createIpnsRecord,
 10  marshalIpnsRecord,
 11  verifyEd25519,
 12  IPNS_SIGNATURE_PREFIX,
 13  concatBytes,
 14} from '@cipherbox/crypto';
 15import { ipnsControllerPublishRecord, ipnsControllerResolveRecord } from '../api/ipns/ipns';
 16
 17/**
 18 * Create an IPNS record locally and publish via backend.
 19 *
 20 * The record is signed locally using the Ed25519 private key, then
 21 * the backend relays it to the IPFS network via delegated routing.
 22 *
 23 * @param params.ipnsPrivateKey - Ed25519 private key (64 bytes in libp2p format)
 24 * @param params.ipnsName - IPNS name (k51.../bafzaa... format)
 25 * @param params.metadataCid - CID of the encrypted metadata blob
 26 * @param params.sequenceNumber - Current sequence number (will be incremented before publish)
 27 * @param params.encryptedIpnsPrivateKey - Hex ECIES-wrapped key for TEE (first publish only)
 28 * @param params.keyEpoch - TEE key epoch (required with encryptedIpnsPrivateKey)
 29 */
 30export async function createAndPublishIpnsRecord(params: {
 31  ipnsPrivateKey: Uint8Array;
 32  ipnsName: string;
 33  metadataCid: string;
 34  sequenceNumber: bigint;
 35  encryptedIpnsPrivateKey?: string;
 36  keyEpoch?: number;
 37}): Promise<{ success: boolean; sequenceNumber: bigint }> {
 38  // 1. Create IPNS record pointing to /ipfs/{metadataCid}
 39  // 24 hour lifetime (will be republished by TEE every 3 hours)
 40  const record = await createIpnsRecord(
 41    params.ipnsPrivateKey,
 42    `/ipfs/${params.metadataCid}`,
 43    params.sequenceNumber,
 44    24 * 60 * 60 * 1000 // 24 hours in ms
 45  );
 46
 47  // 2. Marshal to bytes for transport
 48  const recordBytes = marshalIpnsRecord(record);
 49
 50  // 3. Base64 encode for API transmission
 51  const recordBase64 = btoa(String.fromCharCode(...recordBytes));
 52
 53  // 4. Call backend API to relay to IPFS network
 54  const response = await ipnsControllerPublishRecord({
 55    ipnsName: params.ipnsName,
 56    record: recordBase64,
 57    metadataCid: params.metadataCid,
 58    encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
 59    keyEpoch: params.keyEpoch,
 60  });
 61
 62  return {
 63    success: response.success,
 64    sequenceNumber: BigInt(response.sequenceNumber),
 65  };
 66}
 67
 68/**
 69 * Verify the Ed25519 signature on an IPNS record.
 70 * Per IPFS spec, the signature is over "ipns-signature:" + cborData.
 71 *
 72 * @param signatureV2 - base64 Ed25519 signature (64 bytes)
 73 * @param data - base64 CBOR data that was signed
 74 * @param pubKey - base64 raw Ed25519 public key (32 bytes)
 75 * @returns true if valid
 76 */
 77export async function verifyIpnsSignature(
 78  signatureV2: string,
 79  data: string,
 80  pubKey: string
 81): Promise<boolean> {
 82  const sigBytes = Uint8Array.from(atob(signatureV2), (c) => c.charCodeAt(0));
 83  const dataBytes = Uint8Array.from(atob(data), (c) => c.charCodeAt(0));
 84  const pubKeyBytes = Uint8Array.from(atob(pubKey), (c) => c.charCodeAt(0));
 85
 86  // Per IPFS IPNS spec, signature is over "ipns-signature:" + cborData
 87  const signedData = concatBytes(IPNS_SIGNATURE_PREFIX, dataBytes);
 88  return verifyEd25519(sigBytes, signedData, pubKeyBytes);
 89}
 90
 91/**
 92 * Resolve an IPNS name to its current CID and sequence number.
 93 *
 94 * Calls backend API which relays to delegated-ipfs.dev for resolution.
 95 * When the response includes IPNS signature data (from delegated routing),
 96 * verifies the Ed25519 signature before trusting the CID.
 97 *
 98 * @param ipnsName - IPNS name to resolve (k51.../bafzaa... format)
 99 * @returns Current CID, sequence number, and signature verification status, or null if not found
100 */
101export async function resolveIpnsRecord(
102  ipnsName: string
103): Promise<{ cid: string; sequenceNumber: bigint; signatureVerified: boolean } | null> {
104  try {
105    const response = await ipnsControllerResolveRecord({ ipnsName });
106
107    if (!response.success) {
108      return null;
109    }
110
111    // Verify IPNS signature if all signature fields are present
112    let signatureVerified = false;
113    if (response.signatureV2 && response.data && response.pubKey) {
114      const valid = await verifyIpnsSignature(response.signatureV2, response.data, response.pubKey);
115      if (!valid) {
116        throw new Error('IPNS signature verification failed - record may be tampered');
117      }
118      signatureVerified = true;
119    } else {
120      console.warn('IPNS resolve returned without signature data, skipping verification');
121    }
122
123    return {
124      cid: response.cid,
125      sequenceNumber: BigInt(response.sequenceNumber),
126      signatureVerified,
127    };
128  } catch (error) {
129    // 404 means IPNS name not found - return null
130    // Other errors should propagate (including signature verification failures)
131    if (error instanceof Error && (error as Error & { status?: number }).status === 404) {
132      return null;
133    }
134    throw error;
135  }
136}
