 631    if (isImageFile(name)) {
 632      setImagePreviewDialog({ open: true, item });
 633    } else if (isPdfFile(name)) {
 634      setPdfPreviewDialog({ open: true, item });
 635    } else if (isAudioFile(name)) {
 636      setAudioPlayerDialog({ open: true, item });
 637    } else if (isVideoFile(name)) {
 638      setVideoPlayerDialog({ open: true, item });
 639    }
 640  }, [contextMenu.item]);
 641
 642  // --- Batch action handlers ---
 643
 644  // Batch delete state: stores multiple items for batch confirmation
 645  const [batchDeleteDialog, setBatchDeleteDialog] = useState<{
 646    open: boolean;
 647    items: FolderChild[];
 648  }>({ open: false, items: [] });
 649
 650  // Batch move state: stores items for batch move dialog
 651  const [batchMoveDialog, setBatchMoveDialog] = useState<{
 652    open: boolean;
 653    items: FolderChild[];
 654  }>({ open: false, items: [] });
 655
 656  // Open batch delete confirmation
 657  const handleBatchDeleteClick = useCallback(() => {
 658    if (selectedItems.length === 0) return;
 659    setBatchDeleteDialog({ open: true, items: [...selectedItems] });
 660  }, [selectedItems]);
 661
 662  // Open batch move dialog (uses first selected item for MoveDialog, moves all)
 663  const handleBatchMoveClick = useCallback(() => {
 664    if (selectedItems.length === 0) return;
 665    setBatchMoveDialog({ open: true, items: [...selectedItems] });
 666  }, [selectedItems]);
 667
 668  // Batch download: download all selected files sequentially
 669  const handleBatchDownload = useCallback(async () => {
 670    const files = selectedItems.filter(isFileEntry);
 671    if (files.length === 0) return;
 672
 673    for (const file of files) {
 674      try {
 675        await download({
 676          cid: file.cid,
 677          iv: file.fileIv,
 678          wrappedKey: file.fileKeyEncrypted,
 679          originalName: file.name,
 680        });
 681      } catch (err) {
 682        console.error(`Download failed for ${file.name}:`, err);
 683      }
 684    }
 685  }, [selectedItems, download]);
 686
 687  // Confirm batch delete - single IPNS publish for entire batch
 688  const handleBatchDeleteConfirm = useCallback(async () => {
 689    const items = batchDeleteDialog.items;
 690    if (items.length === 0) return;
 691
 692    try {
 693      await deleteItems(
 694        items.map((i) => ({ id: i.id, type: i.type })),
 695        currentFolderId
 696      );
 697      setBatchDeleteDialog({ open: false, items: [] });
 698      clearSelection();
 699    } catch (err) {
 700      console.error('Batch delete failed:', err);
 701    }
 702  }, [batchDeleteDialog.items, deleteItems, currentFolderId, clearSelection]);
 703
 704  // Confirm batch move - single IPNS publish for source + destination
 705  const handleBatchMoveConfirm = useCallback(
 706    async (destinationFolderId: string) => {
 707      const items = batchMoveDialog.items;
 708      if (items.length === 0) return;
 709
 710      try {
 711        await moveItems(
 712          items.map((i) => ({ id: i.id, type: i.type })),
 713          currentFolderId,
 714          destinationFolderId
 715        );
 716        setBatchMoveDialog({ open: false, items: [] });
 717        clearSelection();
 718      } catch (err) {
 719        console.error('Batch move failed:', err);
 720      }
 721    },
 722    [batchMoveDialog.items, moveItems, currentFolderId, clearSelection]
 723  );
 724
 725  // Close batch dialogs
 726  const closeBatchDeleteDialog = useCallback(() => {
 727    setBatchDeleteDialog({ open: false, items: [] });
 728  }, []);
 729
 730  const closeBatchMoveDialog = useCallback(() => {
 731    setBatchMoveDialog({ open: false, items: [] });
 732  }, []);
 733
 734  // Confirm rename
 735  const handleRenameConfirm = useCallback(
 736    async (newName: string) => {
 737      const item = renameDialog.item;
 738      if (!item) return;
 739
 740      try {
 741        await renameItem(item.id, item.type, newName, currentFolderId);
 742        setRenameDialog({ open: false, item: null });
 743      } catch (err) {
 744        console.error('Rename failed:', err);
 745      }
 746    },
 747    [renameDialog.item, renameItem, currentFolderId]
 748  );
 749
 750  // Confirm delete
 751  const handleDeleteConfirm = useCallback(async () => {
 752    const item = confirmDialog.item;
 753    if (!item) return;
 754
 755    try {
 756      await deleteItem(item.id, item.type, currentFolderId);
 757      setConfirmDialog({ open: false, item: null });
