 762
 763  // Confirm move
 764  const handleMoveConfirm = useCallback(
 765    async (destinationFolderId: string) => {
 766      const item = moveDialog.item;
 767      if (!item) return;
 768
 769      try {
 770        await moveItem(item.id, item.type, currentFolderId, destinationFolderId);
 771        setMoveDialog({ open: false, item: null });
 772      } catch (err) {
 773        console.error('Move failed:', err);
 774      }
 775    },
 776    [moveDialog.item, moveItem, currentFolderId]
 777  );
 778
 779  // Close dialogs
 780  const closeConfirmDialog = useCallback(() => {
 781    setConfirmDialog({ open: false, item: null });
 782  }, []);
 783
 784  const closeRenameDialog = useCallback(() => {
 785    setRenameDialog({ open: false, item: null });
 786  }, []);
 787
 788  const closeMoveDialog = useCallback(() => {
 789    setMoveDialog({ open: false, item: null });
 790  }, []);
 791
 792  const closeDetailsDialog = useCallback(() => {
 793    setDetailsDialog({ open: false, item: null });
 794  }, []);
 795
 796  const closeEditorDialog = useCallback(() => {
 797    setEditorDialog({ open: false, item: null });
 798  }, []);
 799
 800  const closeImagePreviewDialog = useCallback(() => {
 801    setImagePreviewDialog({ open: false, item: null });
 802  }, []);
 803
 804  const closePdfPreviewDialog = useCallback(() => {
 805    setPdfPreviewDialog({ open: false, item: null });
 806  }, []);
 807
 808  const closeAudioPlayerDialog = useCallback(() => {
 809    setAudioPlayerDialog({ open: false, item: null });
 810  }, []);
 811
 812  const closeVideoPlayerDialog = useCallback(() => {
 813    setVideoPlayerDialog({ open: false, item: null });
 814  }, []);
 815
 816  // Create folder handlers
 817  const openCreateFolderDialog = useCallback(() => {
 818    setCreateFolderDialogOpen(true);
 819  }, []);
