 839    confirmDialog.item?.type === 'folder'
 840      ? `Are you sure you want to delete "${confirmDialog.item?.name}"? This will also delete all files and subfolders inside. This cannot be undone.`
 841      : `Are you sure you want to delete "${confirmDialog.item?.name}"? This cannot be undone.`;
 842
 843  const contentClassName = [
