 891        </div>
 892      )}
 893
 894      {/* Initial vault sync state  shown before first IPNS resolve completes (root only) */}
 895      {!isLoading && !initialSyncComplete && currentFolderId === 'root' && !hasChildren && (
 896        <div className="vault-syncing" data-testid="vault-syncing" role="status" aria-live="polite">
 897          <pre className="vault-syncing-ascii" aria-hidden="true">
