 917          showParentRow={currentFolderId !== 'root'}
 918          onNavigateUp={navigateUp}
 919          onSelect={handleSelect}
 920          onSelectAll={handleSelectAll}
 921          onNavigate={handleNavigate}
 922          onContextMenu={handleContextMenu}
 923          onDragStart={handleDragStart}
 924          onDropOnFolder={handleDropOnFolder}
 925          onExternalFileDrop={handleExternalFileDrop}
 926        />
 927      )}
 928
 929      {!isLoading && (initialSyncComplete || currentFolderId !== 'root') && !hasChildren && (
 930        <EmptyState folderId={currentFolderId} />
 931      )}
 932
 933      {/* Selection action bar */}
 934      {multiSelectActive && selectedIds.size > 1 && (
 935        <SelectionActionBar
 936          selectedItems={selectedItems}
 937          isLoading={isOperating || isDownloading}
 938          onClearSelection={clearSelection}
 939          onDownload={handleBatchDownload}
