  1import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
  2import { useNavigate, useParams } from 'react-router-dom';
  3import { unwrapKey, hexToBytes, type FolderEntry } from '@cipherbox/crypto';
  4import { useFolderStore, type FolderNode } from '../stores/folder.store';
