 31 */
 32function getRootFolder(
 33  vaultStore: ReturnType<typeof useVaultStore.getState>,
 34  folders: Record<string, FolderNode>
 35): FolderNode | null {
 36  // If we have an explicit root folder in the tree, use it
 37  const existingRoot = folders['root'];
 38  if (existingRoot) return existingRoot;
 39
 40  // Otherwise construct from vault state
 41  if (!vaultStore.rootFolderKey || !vaultStore.rootIpnsKeypair || !vaultStore.rootIpnsName) {
 42    return null;
 43  }
 44
 45  return {
 46    id: 'root',
 47    name: 'My Vault',
 48    ipnsName: vaultStore.rootIpnsName,
 49    parentId: null,
 50    children: [],
 51    isLoaded: false,
 52    isLoading: false,
