136  // Initialize root folder in store if vault is ready but root not in store
137  useEffect(() => {
138    if (rootFolderKey && rootIpnsKeypair && rootIpnsName && !folders['root']) {
139      const rootFolder = getRootFolder(vaultState, folders);
140      if (rootFolder) {
141        setFolder(rootFolder);
142      }
143    }
144  }, [rootFolderKey, rootIpnsKeypair, rootIpnsName, folders, setFolder, vaultState]);
145
146  // Update store breadcrumbs when local breadcrumbs change
147  useEffect(() => {
148    setBreadcrumbs(breadcrumbs);
149  }, [breadcrumbs, setBreadcrumbs]);
150
151  /**
152   * Navigate to a specific folder using react-router.
153   * Browser history is automatically managed.
154   * Loads subfolder metadata (IPNS resolve + decrypt) if not yet loaded.
155   */
156  const navigateTo = useCallback(
157    async (targetFolderId: string) => {
158      // Track this navigation to detect stale completions from rapid clicks
159      latestNavTarget.current = targetFolderId;
160
161      // Root is always constructible from vault state  just navigate
162      if (targetFolderId === 'root') {
163        navigate('/files');
164        return;
165      }
166
167      // Use getState() to avoid stale Zustand closures in async callback
168      const currentFolders = useFolderStore.getState().folders;
169      const targetFolder = currentFolders[targetFolderId];
170
171      // If already loaded, just navigate
172      if (targetFolder?.isLoaded) {
173        navigate(`/files/${targetFolderId}`);
174        return;
175      }
176
177      // Find the FolderEntry for this subfolder in any loaded parent's children
178      let folderEntry: FolderEntry | undefined;
179      let parentId: string | null = null;
180
181      for (const [fId, fNode] of Object.entries(currentFolders)) {
182        if (!fNode.isLoaded) continue;
183        const match = fNode.children.find(
184          (c): c is FolderEntry => c.type === 'folder' && c.id === targetFolderId
185        );
186        if (match) {
187          folderEntry = match;
188          parentId = fId;
189          break;
190        }
191      }
192
193      if (!folderEntry) {
194        console.error(
195          `Cannot load folder ${targetFolderId}: no parent with its FolderEntry is loaded`
196        );
197        return;
198      }
199
200      // Get user's ECIES private key for unwrapping
201      const vaultKeypair = useAuthStore.getState().vaultKeypair;
202      if (!vaultKeypair) {
203        console.error('Cannot load folder: no vault keypair available');
204        return;
205      }
206
207      // Insert placeholder BEFORE navigating so the component re-render
208      // finds the folder node already in the store (avoids undefined flash)
209      setIsLoading(true);
210      const loadingPlaceholder: FolderNode = {
211        id: targetFolderId,
212        name: folderEntry.name,
213        ipnsName: folderEntry.ipnsName,
214        parentId,
215        children: [],
216        isLoaded: false,
217        isLoading: true,
218        sequenceNumber: 0n,
219        folderKey: new Uint8Array(0),
220        ipnsPrivateKey: new Uint8Array(0),
221      };
222      useFolderStore.getState().setFolder(loadingPlaceholder);
223
224      // Now navigate  the placeholder is already in the store
