226
227      try {
228        // Unwrap keys using user's ECIES private key
229        const folderKey = await unwrapKey(
230          hexToBytes(folderEntry.folderKeyEncrypted),
231          vaultKeypair.privateKey
232        );
233        const ipnsPrivateKey = await unwrapKey(
234          hexToBytes(folderEntry.ipnsPrivateKeyEncrypted),
235          vaultKeypair.privateKey
236        );
237
238        // Load folder metadata from IPNS (retry if IPNS hasn't propagated yet)
239        const MAX_RETRIES = 3;
240        const RETRY_DELAY_MS = 2000;
241        let folderNode: Awaited<ReturnType<typeof loadFolder>> | null = null;
242
243        for (let attempt = 0; attempt <= MAX_RETRIES; attempt++) {
244          if (latestNavTarget.current !== targetFolderId) return;
245
246          folderNode = await loadFolder(
247            targetFolderId,
248            folderKey,
249            ipnsPrivateKey,
250            folderEntry.ipnsName,
251            parentId,
252            folderEntry.name
253          );
254
255          // If loaded successfully or this is the last attempt, stop retrying
256          if (folderNode.isLoaded || attempt === MAX_RETRIES) break;
257
258          // IPNS not propagated yet  wait and retry
259          await new Promise((r) => setTimeout(r, RETRY_DELAY_MS));
260        }
261
262        // Only apply result if this is still the latest navigation
263        if (latestNavTarget.current !== targetFolderId) return;
264
265        useFolderStore.getState().setFolder(folderNode!);
266      } catch (err) {
267        console.error('Failed to load subfolder:', err);
268        // Only clean up if this is still the latest navigation
269        if (latestNavTarget.current !== targetFolderId) return;
270        // Remove the loading placeholder so the user can retry
271        useFolderStore.getState().removeFolder(targetFolderId);
272      } finally {
273        // Only clear loading if this is still the latest navigation
274        if (latestNavTarget.current === targetFolderId) {
275          setIsLoading(false);
276        }
277      }
278    },
279    [navigate]
280  );
281
282  /**
283   * Navigate up to parent folder.
284   */
285  const navigateUp = useCallback(() => {
286    if (currentFolder?.parentId) {
287      navigateTo(currentFolder.parentId);
288    } else if (currentFolderId !== 'root') {
289      navigateTo('root');
290    }
291  }, [currentFolder, currentFolderId, navigateTo]);
292
293  return {
294    currentFolderId,
295    currentFolder,
296    breadcrumbs,
297    isLoading,
298    navigateTo,
299    navigateUp,
300  };
301}
