  1/**
  2 * Mock Delegated Routing Service for E2E Testing
  3 *
  4 * Implements the IPFS delegated routing API for IPNS records:
  5 * - PUT /routing/v1/ipns/{name} - Store an IPNS record
  6 * - GET /routing/v1/ipns/{name} - Retrieve an IPNS record
  7 *
  8 * Records are stored in-memory and reset when the service restarts.
  9 * This allows E2E tests to run without polluting the public IPFS DHT
 10 * and avoids sequence number conflicts from repeated test runs.
 11 */
 12
 13import Fastify from 'fastify';
 14
 15const fastify = Fastify({
 16  logger: {
 17    level: process.env.LOG_LEVEL ?? 'info',
 18    transport:
 19      process.env.NODE_ENV !== 'production'
 20        ? { target: 'pino-pretty', options: { colorize: true } }
 21        : undefined,
 22  },
 23});
 24
 25// In-memory storage for IPNS records
 26// Key: IPNS name (k51... or bafzaa...), Value: raw record bytes
 27const ipnsRecords = new Map<string, Buffer>();
 28
 29// Health check endpoint
 30fastify.get('/health', async () => {
 31  return { status: 'ok', records: ipnsRecords.size };
 32});
 33
 34// GET /routing/v1/ipns/{name} - Retrieve IPNS record
 35fastify.get<{ Params: { name: string } }>('/routing/v1/ipns/:name', async (request, reply) => {
 36  const { name } = request.params;
 37
 38  const record = ipnsRecords.get(name);
 39  if (!record) {
 40    return reply.status(404).send({
 41      error: 'record not found',
 42      name,
 43    });
 44  }
 45
 46  fastify.log.info({ name, size: record.length }, 'Retrieved IPNS record');
 47
 48  return reply.status(200).header('Content-Type', 'application/vnd.ipfs.ipns-record').send(record);
 49});
 50
 51// PUT /routing/v1/ipns/{name} - Store IPNS record
 52fastify.put<{ Params: { name: string } }>('/routing/v1/ipns/:name', async (request, reply) => {
 53  const { name } = request.params;
 54
 55  // Get raw body as Buffer
 56  const body = request.body as Buffer;
 57  if (!body || body.length === 0) {
 58    return reply.status(400).send({
 59      error: 'empty request body',
 60    });
 61  }
 62
 63  // Store the record (overwrites any existing record)
 64  // Note: We intentionally don't validate sequence numbers here.
 65  // This allows tests to reset state by simply restarting the service.
 66  ipnsRecords.set(name, body);
 67
 68  fastify.log.info(
 69    { name, size: body.length, totalRecords: ipnsRecords.size },
 70    'Stored IPNS record'
 71  );
 72
 73  return reply.status(200).send({ ok: true });
 74});
 75
 76// Configure to accept raw binary bodies
