  1/**
  2 * useDeviceApproval Hook
  3 *
  4 * Manages the full lifecycle for BOTH sides of the cross-device approval flow:
  5 *
  6 * **New device (requester):**
  7 * - Generate ephemeral secp256k1 keypair
  8 * - Create bulletin board request
  9 * - Poll for approval status
 10 * - On approval: ECIES-decrypt factor key, inputFactorKey, create device factor
 11 *
 12 * **Existing device (approver):**
 13 * - Poll for pending approval requests
 14 * - Approve: ECIES-encrypt current factor key with requester's ephemeral public key
 15 * - Deny: mark request as denied
 16 */
 17
 18import { useCallback, useEffect, useRef, useState } from 'react';
 19import * as secp256k1 from '@noble/secp256k1';
 20import {
 21  generateFactorKey,
 22  TssShareType,
 23  FactorKeyTypeShareDescription,
 24} from '@web3auth/mpc-core-kit';
 25import { wrapKey, unwrapKey, hexToBytes, bytesToHex } from '@cipherbox/crypto';
 26import { useCoreKit } from '../lib/web3auth/core-kit-provider';
 27import { useMfa } from './useMfa';
 28import { useVisibility } from './useVisibility';
 29import { useAuth } from './useAuth';
 30import { getOrCreateDeviceIdentity } from '../lib/device/identity';
 31import { detectDeviceInfo } from '../lib/device/info';
 32import {
 33  deviceApprovalApi,
 34  type ApprovalStatusResponse,
 35  type PendingApproval,
 36} from '../services/device-approval.service';
 37
 38export type ApprovalStatus =
 39  | 'idle'
 40  | 'requesting'
 41  | 'pending'
 42  | 'approved'
 43  | 'denied'
 44  | 'expired'
 45  | 'completing'
 46  | 'error';
 47
 48export function useDeviceApproval() {
 49  const { coreKit } = useCoreKit();
 50  const { inputFactorKey } = useMfa();
 51  const { completeRequiredShare } = useAuth();
 52  const isVisible = useVisibility();
 53
 54  // --- New device (requester) state ---
 55  const [approvalStatus, setApprovalStatus] = useState<ApprovalStatus>('idle');
 56  const [approvalError, setApprovalError] = useState<string | null>(null);
 57  const requestIdRef = useRef<string | null>(null);
 58  const ephemeralPrivKeyRef = useRef<Uint8Array | null>(null);
 59  const requesterPollRef = useRef<ReturnType<typeof setInterval> | null>(null);
 60
 61  // --- Existing device (approver) state ---
 62  const [pendingRequests, setPendingRequests] = useState<PendingApproval[]>([]);
 63  const [isPollingPending, setIsPollingPending] = useState(false);
 64  const approverPollRef = useRef<ReturnType<typeof setInterval> | null>(null);
 65
 66  /**
 67   * Zero-fill and clear ephemeral private key from memory.
 68   */
 69  const clearEphemeralKey = useCallback(() => {
 70    if (ephemeralPrivKeyRef.current) {
 71      ephemeralPrivKeyRef.current.fill(0);
 72      ephemeralPrivKeyRef.current = null;
 73    }
 74  }, []);
 75
 76  /**
 77   * Stop requester polling.
 78   */
 79  const stopRequesterPolling = useCallback(() => {
 80    if (requesterPollRef.current) {
 81      clearInterval(requesterPollRef.current);
 82      requesterPollRef.current = null;
 83    }
 84  }, []);
 85
 86  /**
 87   * Stop approver polling.
 88   */
 89  const stopApproverPolling = useCallback(() => {
 90    if (approverPollRef.current) {
 91      clearInterval(approverPollRef.current);
 92      approverPollRef.current = null;
 93    }
 94    setIsPollingPending(false);
 95  }, []);
 96
 97  // =========================================================================
 98  // NEW DEVICE SIDE (REQUESTER)
 99  // =========================================================================
100
101  /**
102   * Handle a successful approval: decrypt factor key, input it, create device factor.
103   */
104  const handleApprovalSuccess = useCallback(
105    async (encryptedFactorKeyHex: string) => {
106      setApprovalStatus('completing');
107
108      try {
109        const ephPrivKey = ephemeralPrivKeyRef.current;
110        if (!ephPrivKey) {
111          throw new Error('Ephemeral private key not available');
112        }
113        if (!coreKit) {
114          throw new Error('Core Kit not initialized');
115        }
116
117        // 1. ECIES-decrypt the factor key
118        const encrypted = hexToBytes(encryptedFactorKeyHex);
119        const factorKeyBytes = await unwrapKey(encrypted, ephPrivKey);
120        const factorKeyHex = bytesToHex(factorKeyBytes);
121        factorKeyBytes.fill(0); // Zero-fill decrypted key material after conversion
122
123        // 2. Input the factor key to complete Core Kit login
124        await inputFactorKey(factorKeyHex);
125
126        // 3. Create device factor for this new device
127        const newDeviceFactor = generateFactorKey();
128        await coreKit.createFactor({
129          shareType: TssShareType.DEVICE,
130          factorKey: newDeviceFactor.private,
131          shareDescription: FactorKeyTypeShareDescription.DeviceShare,
132        });
133        await coreKit.setDeviceFactor(newDeviceFactor.private);
134        await coreKit.commitChanges();
135
136        // 4. Clean up ephemeral key
137        clearEphemeralKey();
138
139        // 5. Complete the login flow
140        await completeRequiredShare();
141
142        setApprovalStatus('approved');
143      } catch (err) {
144        stopRequesterPolling();
145        clearEphemeralKey();
146        setApprovalError(err instanceof Error ? err.message : 'Failed to complete approval');
147        setApprovalStatus('error');
148      }
149    },
150    [coreKit, inputFactorKey, completeRequiredShare, clearEphemeralKey, stopRequesterPolling]
151  );
152
153  /**
154   * Start polling for approval status (new device side).
155   */
156  const startRequesterPolling = useCallback(
157    (requestId: string) => {
158      stopRequesterPolling();
159
160      const poll = async () => {
161        // Separate network fetch from success handling so network errors
162        // don't swallow failures from handleApprovalSuccess/completeRequiredShare
163        let result: ApprovalStatusResponse;
164        try {
165          result = await deviceApprovalApi.getStatus(requestId);
166        } catch {
167          // Network error -- continue polling, don't crash
168          return;
169        }
170
171        if (result.status === 'approved' && result.encryptedFactorKey) {
172          stopRequesterPolling();
173          await handleApprovalSuccess(result.encryptedFactorKey);
174        } else if (result.status === 'denied') {
175          stopRequesterPolling();
176          clearEphemeralKey();
177          setApprovalStatus('denied');
178        } else if (result.status === 'expired') {
179          stopRequesterPolling();
180          clearEphemeralKey();
181          setApprovalStatus('expired');
182        }
183        // 'pending' -> continue polling
184      };
185
186      requesterPollRef.current = setInterval(poll, 3000);
187      // Fire immediately
188      void poll();
189    },
190    [stopRequesterPolling, handleApprovalSuccess, clearEphemeralKey]
191  );
192
193  /**
194   * Create an approval request on the bulletin board (new device side).
195   * Generates an ephemeral keypair and starts polling.
196   */
197  const requestApproval = useCallback(async () => {
198    setApprovalStatus('requesting');
199    setApprovalError(null);
200
201    try {
202      // 1. Generate ephemeral secp256k1 keypair
203      const ephemeral = secp256k1.keygen();
204      ephemeralPrivKeyRef.current = ephemeral.secretKey;
205      const ephemeralPubKeyHex = bytesToHex(ephemeral.publicKey);
206
207      // 2. Get device identity
208      const deviceIdentity = await getOrCreateDeviceIdentity();
209
210      // 3. Get device name
211      const deviceInfo = detectDeviceInfo();
212
213      // 4. Create request on bulletin board
214      const { requestId } = await deviceApprovalApi.createRequest({
215        deviceId: deviceIdentity.deviceId,
216        deviceName: deviceInfo.name,
217        ephemeralPublicKey: ephemeralPubKeyHex,
218      });
219
220      requestIdRef.current = requestId;
221      setApprovalStatus('pending');
222
223      // 5. Start polling for response
224      startRequesterPolling(requestId);
225    } catch (err) {
226      clearEphemeralKey();
227      setApprovalError(err instanceof Error ? err.message : 'Failed to create approval request');
228      setApprovalStatus('error');
229    }
230  }, [startRequesterPolling, clearEphemeralKey]);
231
232  /**
233   * Cancel the pending approval request (new device side).
234   * Also called when recovery phrase is used instead (RESEARCH.md Pitfall 5).
235   */
236  const cancelRequest = useCallback(async () => {
237    stopRequesterPolling();
238
239    if (requestIdRef.current) {
240      try {
241        await deviceApprovalApi.cancel(requestIdRef.current);
242      } catch {
243        // Best-effort cleanup -- request may already be expired/cancelled
244      }
245      requestIdRef.current = null;
246    }
247
248    clearEphemeralKey();
249    setApprovalStatus('idle');
250  }, [stopRequesterPolling, clearEphemeralKey]);
251
252  // =========================================================================
253  // EXISTING DEVICE SIDE (APPROVER)
254  // =========================================================================
255
256  /**
257   * Start polling for pending approval requests (existing device side).
258   */
259  const pollPendingRequests = useCallback(() => {
260    if (isPollingPending) return;
261    setIsPollingPending(true);
262
263    const poll = async () => {
264      try {
265        const pending = await deviceApprovalApi.getPending();
266        setPendingRequests(pending);
267      } catch {
268        // Network error -- continue polling
269      }
270    };
271
272    // Poll every 5 seconds
273    approverPollRef.current = setInterval(poll, 5000);
274    // Fire immediately
275    void poll();
276  }, [isPollingPending]);
277
278  /**
279   * Approve a pending request: ECIES-encrypt current factor key with
280   * the requester's ephemeral public key and send the response.
281   */
282  const approveRequest = useCallback(
283    async (requestId: string, ephemeralPublicKeyHex: string) => {
284      if (!coreKit) throw new Error('Core Kit not initialized');
285
286      // 1. Get current factor key
287      const factorKeyResult = coreKit.getCurrentFactorKey();
288      if (!factorKeyResult?.factorKey) {
289        throw new Error('No active factor key available to share');
290      }
291      const factorKeyHex = factorKeyResult.factorKey.toString('hex').padStart(64, '0');
292      const factorKeyBytes = hexToBytes(factorKeyHex);
293
294      // 2. ECIES-encrypt with the requester's ephemeral public key
295      const ephemeralPubKey = hexToBytes(ephemeralPublicKeyHex);
296      const encrypted = await wrapKey(factorKeyBytes, ephemeralPubKey);
297      factorKeyBytes.fill(0); // Zero-fill factor key bytes after wrapping
298
299      // 3. Get current device ID for tracking
300      const deviceIdentity = await getOrCreateDeviceIdentity();
301
302      // 4. Send response
303      await deviceApprovalApi.respond(requestId, {
304        action: 'approve',
305        encryptedFactorKey: bytesToHex(encrypted),
306        respondedByDeviceId: deviceIdentity.deviceId,
307      });
308
309      // 5. Remove from pending list
310      setPendingRequests((prev) => prev.filter((r) => r.requestId !== requestId));
311    },
312    [coreKit]
313  );
314
315  /**
316   * Deny a pending approval request.
317   */
318  const denyRequest = useCallback(async (requestId: string) => {
319    // Get current device ID for tracking
320    const deviceIdentity = await getOrCreateDeviceIdentity();
321
322    await deviceApprovalApi.respond(requestId, {
323      action: 'deny',
324      respondedByDeviceId: deviceIdentity.deviceId,
325    });
326
327    // Remove from pending list
328    setPendingRequests((prev) => prev.filter((r) => r.requestId !== requestId));
329  }, []);
330
331  // =========================================================================
332  // VISIBILITY-BASED POLLING CONTROL (approver)
333  // =========================================================================
334
335  // Pause/resume approver polling based on tab visibility
336  useEffect(() => {
337    if (!isPollingPending) return;
338
339    if (isVisible) {
340      // Resume polling when tab becomes visible
341      if (!approverPollRef.current) {
342        const poll = async () => {
343          try {
344            const pending = await deviceApprovalApi.getPending();
345            setPendingRequests(pending);
346          } catch {
347            // Network error -- continue polling
348          }
349        };
350        approverPollRef.current = setInterval(poll, 5000);
351        void poll();
352      }
353    } else {
354      // Pause polling when tab is hidden
355      if (approverPollRef.current) {
356        clearInterval(approverPollRef.current);
357        approverPollRef.current = null;
358      }
359    }
360  }, [isVisible, isPollingPending]);
361
362  // =========================================================================
363  // CLEANUP ON UNMOUNT
364  // =========================================================================
365
366  useEffect(() => {
367    return () => {
368      stopRequesterPolling();
369      stopApproverPolling();
370      clearEphemeralKey();
371    };
372  }, [stopRequesterPolling, stopApproverPolling, clearEphemeralKey]);
373
374  return {
375    // Requester (new device)
376    requestApproval,
377    cancelRequest,
378    approvalStatus,
379    approvalError,
380
381    // Approver (existing device)
382    pollPendingRequests,
383    stopApproverPolling,
384    approveRequest,
385    denyRequest,
386    pendingRequests,
387    isPolling: isPollingPending,
388  };
389}
