 1import { useAuthStore } from '../stores/auth.store';
 2
 3// Use VITE_API_URL if set, otherwise default to same-origin /api path for deployed environments
 4const BASE_URL =
 5  import.meta.env.VITE_API_URL ||
 6  (typeof window !== 'undefined' ? `${window.location.origin}/api` : '/api');
 7
 8export const customInstance = async <T>(config: {
 9  url: string;
10  method: 'GET' | 'POST' | 'PUT' | 'DELETE' | 'PATCH';
11  params?: Record<string, string>;
12  data?: unknown;
13  headers?: Record<string, string>;
14  signal?: AbortSignal;
15  responseType?: 'json' | 'blob' | 'text';
16}): Promise<T> => {
17  const { url, method, params, data, headers, signal, responseType = 'json' } = config;
18
19  // Get auth token from store
20  const accessToken = useAuthStore.getState().accessToken;
21
22  const queryString = params ? '?' + new URLSearchParams(params).toString() : '';
23
24  const response = await fetch(`${BASE_URL}${url}${queryString}`, {
25    method,
26    headers: {
27      // Only set Content-Type for JSON requests with body
28      ...(data && responseType === 'json' ? { 'Content-Type': 'application/json' } : {}),
29      ...(accessToken ? { Authorization: `Bearer ${accessToken}` } : {}),
30      ...headers,
31    },
32    body: data ? JSON.stringify(data) : undefined,
33    signal,
34  });
35
36  if (!response.ok) {
37    const err = new Error(`HTTP error! status: ${response.status}`);
38    (err as Error & { status: number }).status = response.status;
39    throw err;
40  }
41
42  // Return appropriate response type
43  if (responseType === 'blob') {
44    return response.blob() as Promise<T>;
45  } else if (responseType === 'text') {
46    return response.text() as Promise<T>;
47  }
48  return response.json();
49};
50
51export default customInstance;
52

