() => {
  const bar = document.querySelector('.selection-action-bar');
  const downloadBtn = document.querySelector('.selection-action-bar .toolbar-btn--secondary');
  const deleteBtn = document.querySelector('.selection-action-bar-delete');
  const count = document.querySelector('.selection-action-bar-count');
  const clear = document.querySelector('.selection-action-bar-clear');
  const s = (el) => {
    const cs = getComputedStyle(el);
    return {
      bg: cs.backgroundColor, color: cs.color, borderLeft: cs.borderLeftColor,
      borderTop: cs.borderTopColor, borderTopWidth: cs.borderTopWidth,
      fontSize: cs.fontSize, fontWeight: cs.fontWeight, fontFamily: cs.fontFamily,
      padding: cs.padding, gap: cs.gap, height: cs.height
    };
  };
  return { bar: s(bar), downloadBtn: s(downloadBtn), deleteBtn: s(deleteBtn), count: s(count), clear: s(clear) };
}

