/**
 * File Browser Component Styles - Terminal Aesthetic
 *
 * Styling for the file browser UI including:
 * - Main layout (sidebar + content)
 * - Folder tree navigation
 * - File list with columns
 * - Empty state drop zone
 */

/* ==========================================================================
   File Browser Layout
   ========================================================================== */

/* ==========================================================================
   DEPRECATED: Old file-browser sidebar layout - replaced in Phase 6.3
   ========================================================================== */
/*
.file-browser {
  display: flex;
  flex: 1;
  min-height: 0;
  background-color: var(--color-background);
}

.file-browser-sidebar {
  width: 250px;
  min-width: 200px;
  max-width: 350px;
  border-right: var(--border-thickness) solid var(--color-border);
  overflow-y: auto;
  background-color: var(--color-background);
}

.file-browser-main {
  flex: 1;
  min-width: 0;
  overflow-y: auto;
  padding: var(--spacing-md);
  background-color: var(--color-background);
}
*/

/* File Browser Content - now without sidebar (Phase 6.3) */
.file-browser-content {
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow-x: hidden;
  overflow-y: auto;
  background-color: var(--color-background);
  position: relative;
}

/* External drag active state — shown when files from OS are dragged over */
.file-browser-content--drag-active {
  outline: 2px dashed var(--color-green-primary);
  outline-offset: -2px;
  background-color: rgba(0, 208, 132, 0.03);
}

.file-browser-toolbar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--spacing-md);
  padding: var(--spacing-sm) 20px;
  background-color: var(--color-toolbar-bg);
}

/* Toolbar buttons - Terminal flag style */
.file-browser-toolbar button,
.toolbar-button {
  padding: var(--spacing-xs) var(--spacing-md);
  font-family: var(--font-family-mono);
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-normal);
  border-radius: 0;
  cursor: pointer;
  transition: box-shadow 0.15s ease;
}

/* Primary action button (--upload) */
.toolbar-button--primary,
.file-browser-toolbar .btn-primary {
  background-color: var(--color-green-primary);
  color: var(--color-black);
  border: none;
  font-weight: var(--font-weight-semibold);
}

.toolbar-button--primary:hover,
.file-browser-toolbar .btn-primary:hover {
  box-shadow: var(--glow-green);
}

/* Secondary action buttons (--new-dir, --refresh) */
.toolbar-button--secondary,
.file-browser-toolbar .btn-secondary {
  background-color: transparent;
  color: var(--color-text-primary);
  border: var(--border-thickness) solid var(--color-border);
}

.toolbar-button--secondary:hover,
.file-browser-toolbar .btn-secondary:hover {
  box-shadow: var(--glow-green);
}

.file-browser-loading {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 200px;
  color: var(--color-text-secondary);
  font-family: var(--font-family-mono);
}

.file-browser-loading-spinner {
  font-size: var(--font-size-sm);
}

.file-browser-actions {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-left: auto;
}

/* Terminal-style toolbar buttons */
.toolbar-btn {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 6px 12px;
  background: transparent;
  border: var(--border-thickness) solid;
  border-radius: 0;
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-normal);
  cursor: pointer;
  transition: box-shadow 0.15s ease;
}

.toolbar-btn--primary {
  color: var(--color-text-primary);
  border-color: var(--color-text-primary);
}

.toolbar-btn--primary:hover:not(:disabled) {
  box-shadow: var(--glow-green);
}

.toolbar-btn--secondary {
  color: var(--color-text-secondary);
  border-color: var(--color-border-dim);
}

.toolbar-btn--secondary:hover:not(:disabled) {
  box-shadow: 0 0 8px rgba(0, 208, 132, 0.2);
}

.toolbar-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Legacy button class - map to new styles */
.file-browser-new-folder-button {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 6px 12px;
  background: transparent;
  border: var(--border-thickness) solid var(--color-text-primary);
  border-radius: 0;
  color: var(--color-text-primary);
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  cursor: pointer;
  transition: box-shadow 0.15s ease;
}

.file-browser-new-folder-button:hover:not(:disabled) {
  box-shadow: var(--glow-green);
}

.file-browser-new-folder-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.file-browser-new-folder-button svg {
  flex-shrink: 0;
}

/* Toolbar upload - compact button style */
.toolbar-upload {
  display: flex;
}

.toolbar-upload .upload-zone-wrapper {
  width: auto;
}

.toolbar-upload .upload-zone {
  min-height: auto;
  padding: 6px 12px;
  border-style: solid;
  border-color: var(--color-border-dim);
}

.toolbar-upload .upload-zone:hover {
  border-color: var(--color-border-dim);
  box-shadow: 0 0 8px rgba(0, 208, 132, 0.2);
}

.toolbar-upload .upload-zone-content {
  flex-direction: row;
  gap: 6px;
}

.toolbar-upload .upload-zone-icon {
  font-size: var(--font-size-md);
  color: var(--color-text-secondary);
}

.toolbar-upload .upload-zone-text {
  font-size: var(--font-size-xs);
  color: var(--color-text-secondary);
}

/* ==========================================================================
   DEPRECATED: Folder Tree styles - no longer used as of Phase 6.3
   FolderTree replaced by in-place navigation with ParentDirRow
   ========================================================================== */
/*
.folder-tree { ... }
.folder-tree-header { ... }
.folder-tree-title { ... }
.folder-tree-content { ... }
.folder-tree-placeholder { ... }
.folder-tree-node { ... }
.folder-tree-item { ... }
.folder-tree-toggle { ... }
.folder-tree-icon { ... }
.folder-tree-name { ... }
.folder-tree-loading { ... }
*/

/* ==========================================================================
   File List
   ========================================================================== */

.file-list {
  display: flex;
  flex-direction: column;
  flex: 1;
  min-height: 0;
  border: var(--border-thickness) solid var(--color-border);
}

.file-list-header {
  display: grid;
  grid-template-columns: 1fr 120px 180px;
  gap: var(--spacing-md);
  padding: var(--spacing-sm) var(--spacing-md);
  border-bottom: var(--border-thickness) solid var(--color-border);
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-semibold);
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--color-text-primary);
  background-color: var(--color-background);
}

.file-list-header-name {
  display: flex;
  align-items: center;
}

.file-list-body {
  flex: 1;
  min-height: 0;
  overflow-y: auto;
}

/* File List Item - Desktop uses grid, Mobile uses 2-row stacked layout */
.file-list-item {
  display: grid;
  grid-template-columns: 1fr 120px 180px;
  grid-template-areas: "name size date";
  gap: var(--spacing-md);
  padding: var(--spacing-sm) var(--spacing-md);
  cursor: pointer;
  transition: background-color 0.15s ease;
  border-bottom: var(--border-thickness) solid var(--color-border-dim);
  font-family: var(--font-family-mono);
}

.file-list-item:last-child {
  border-bottom: none;
}

.file-list-item:hover {
  background-color: var(--color-green-darker);
}

.file-list-item--selected {
  background-color: var(--color-green-darker);
}

.file-list-item--selected:hover {
  background-color: var(--color-green-darker);
}

/* Drag-drop target state for folders */
.file-list-item--drag-over {
  background-color: var(--color-green-darker);
  outline: 1px dashed var(--color-green-primary);
  outline-offset: -1px;
}

/* Parent directory row [..] PARENT_DIR */
.file-list-item--parent {
  cursor: pointer;
}

.file-list-item--parent:hover {
  background-color: var(--color-green-darker);
}

/* Row wrappers - on desktop, these act as containers within grid */
.file-list-item-row-top {
  grid-area: name;
  display: flex;
  align-items: center;
  gap: 6px;
  min-width: 0;
}

.file-list-item-row-bottom {
  display: contents; /* Children participate directly in parent grid on desktop */
}

.file-list-item-icon {
  font-family: var(--font-family-mono);
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-semibold);
  flex-shrink: 0;
  color: var(--color-text-primary);
}

.file-list-item-name {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  color: var(--color-text-primary);
  font-size: var(--font-size-sm);
}

.file-list-item-size {
  grid-area: size;
  display: flex;
  align-items: center;
  color: var(--color-text-secondary);
  font-size: var(--font-size-sm);
}

/* TYPE column removed - no longer used in 3-column layout
.file-list-item-type {
  grid-area: type;
  display: flex;
  align-items: center;
  color: var(--color-text-secondary);
  font-size: var(--font-size-sm);
  text-transform: lowercase;
}
*/

.file-list-item-date {
  grid-area: date;
  display: flex;
  align-items: center;
  color: var(--color-text-secondary);
  font-size: var(--font-size-sm);
}

/* ==========================================================================
   Vault Syncing State
   ========================================================================== */

.vault-syncing {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 300px;
  margin: var(--spacing-lg);
  text-align: center;
  padding: var(--spacing-lg);
}

.vault-syncing-ascii {
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  line-height: 1.6;
  color: var(--color-green-primary);
  margin: 0 0 var(--spacing-md) 0;
  white-space: pre;
  opacity: 0.8;
}

.vault-syncing-bar {
  width: 200px;
  height: 4px;
  background: var(--color-border);
  margin-bottom: var(--spacing-md);
  overflow: hidden;
}

.vault-syncing-bar-fill {
  width: 40%;
  height: 100%;
  background: var(--color-green-primary);
  animation: vault-syncing-slide 1.5s ease-in-out infinite;
}

@keyframes vault-syncing-slide {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(350%);
  }
}

@media (prefers-reduced-motion: reduce) {
  .vault-syncing-bar-fill {
    animation: none;
  }
}

.vault-syncing-text {
  font-family: var(--font-family-mono);
  font-size: var(--font-size-sm);
  margin: 0 0 var(--spacing-xs) 0;
  color: var(--color-text-muted);
}

.vault-syncing-hint {
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  margin: 0;
  color: var(--color-text-dim);
  text-transform: lowercase;
}

/* ==========================================================================
   Empty State
   ========================================================================== */

.empty-state {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 300px;
  border: var(--border-thickness) dashed var(--color-border);
  border-radius: 0;
  margin: var(--spacing-lg);
  cursor: pointer;
  transition: border-color 0.2s ease, background-color 0.2s ease;
}

.empty-state:hover,
.empty-state-drag-active {
  border-color: var(--color-green-primary);
  background-color: var(--color-green-darker);
}

.empty-state-uploading {
  opacity: 0.7;
  pointer-events: none;
}

.empty-state-content {
  text-align: center;
  padding: var(--spacing-lg);
}

/* Terminal-style ASCII art for empty state */
.empty-state-ascii {
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  line-height: 1.2;
  color: var(--color-green-primary);
  margin: 0 0 var(--spacing-md) 0;
  white-space: pre;
}

/* Old icon style - replaced by ASCII art */
.empty-state-icon {
  font-size: 2rem;
  display: block;
  margin-bottom: var(--spacing-md);
  color: var(--color-text-secondary);
}

.empty-state-text {
  font-family: var(--font-family-mono);
  font-size: var(--font-size-sm);
  margin: 0 0 var(--spacing-xs) 0;
  color: var(--color-text-muted);
}

.empty-state-hint {
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  margin: 0;
  color: var(--color-text-dim);
  text-transform: lowercase;
}

/* ==========================================================================
   Checkboxes (Multi-select)
   ========================================================================== */

/* Header checkbox */
.file-list-header-checkbox {
  display: inline;
  cursor: pointer;
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-semibold);
  color: var(--color-text-secondary);
  margin-right: 6px;
  flex-shrink: 0;
}

.file-list-header-checkbox:hover {
  color: var(--color-green-primary);
}

.file-list-header-checkbox:focus-visible {
  outline: 2px solid var(--color-green-primary);
  outline-offset: 1px;
}

/* Item checkbox */
.file-list-item-checkbox {
  display: inline;
  cursor: pointer;
  font-family: var(--font-family-mono);
  font-size: var(--font-size-sm);
  color: var(--color-text-secondary);
  margin-right: 4px;
  flex-shrink: 0;
}

.file-list-item-checkbox:hover {
  color: var(--color-green-primary);
}

.file-list-item-checkbox:focus-visible {
  outline: 2px solid var(--color-green-primary);
  outline-offset: 1px;
}

/* ==========================================================================
   Selection Action Bar
   ========================================================================== */

.selection-action-bar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--spacing-md);
  padding: var(--spacing-xs) 20px;
  background-color: var(--color-green-darker);
  border-top: var(--border-thickness) solid var(--color-green-primary);
  font-family: var(--font-family-mono);
  font-size: var(--font-size-sm);
}

.selection-action-bar-info {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
}

.selection-action-bar-count {
  color: var(--color-green-primary);
  font-weight: var(--font-weight-semibold);
}

.selection-action-bar-clear {
  background: none;
  border: none;
  color: var(--color-text-secondary);
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  cursor: pointer;
  padding: 2px 4px;
}

.selection-action-bar-clear:hover {
  color: var(--color-text-primary);
}

.selection-action-bar-clear:focus-visible {
  outline: 2px solid var(--color-green-primary);
  outline-offset: 1px;
}

.selection-action-bar-actions {
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
}

.selection-action-bar .toolbar-btn--secondary {
  color: var(--color-green-primary);
  border-color: var(--color-green-dim);
}

.selection-action-bar .toolbar-btn--secondary:hover:not(:disabled) {
  box-shadow: var(--glow-green);
}

.selection-action-bar-delete {
  color: var(--color-error);
  border-color: var(--color-error);
}

.selection-action-bar-delete:hover:not(:disabled) {
  box-shadow: 0 0 8px rgb(255 0 0 / 20%);
}

.selection-action-bar-delete:focus-visible {
  outline: 2px solid var(--color-error);
  outline-offset: 1px;
}

/* ==========================================================================
   Context Menu Multi-select Header
   ========================================================================== */

.context-menu-header {
  padding: var(--spacing-xs) var(--spacing-sm);
  font-family: var(--font-family-mono);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-semibold);
  color: var(--color-green-primary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

/* Note: Mobile responsive styles are in responsive.css */

