     1→import { useHealthControllerCheck } from '../../api/health/health';
     2→
     3→/**
     4→ * Status indicator component.
     5→ * Shows API connection status in the footer with a glowing dot and terminal-style text.
     6→ */
     7→export function StatusIndicator() {
     8→  const { data, isLoading, isError } = useHealthControllerCheck({
     9→    query: {
    10→      refetchInterval: 30000,
    11→      retry: 2,
    12→      refetchOnWindowFocus: true,
    13→    },
    14→  });
    15→
    16→  // Determine status based on query state
    17→  const isConnected = !isLoading && !isError && data?.status === 'ok';
    18→
    19→  let dotClass = 'status-indicator-dot';
    20→  let statusText: string;
    21→
    22→  if (isLoading) {
    23→    dotClass += ' status-indicator-dot--loading';
    24→    statusText = '[CHECKING]';
    25→  } else if (isConnected) {
    26→    dotClass += ' status-indicator-dot--connected';
    27→    statusText = '[CONNECTED]';
    28→  } else {
    29→    dotClass += ' status-indicator-dot--disconnected';
    30→    statusText = '[DISCONNECTED]';
    31→  }
    32→
    33→  return (
    34→    <div className="status-indicator" data-testid="status-indicator">
    35→      <span className={dotClass} />
    36→      <span className="status-indicator-text">{statusText}</span>
    37→    </div>
    38→  );
    39→}
