 1â†’/**
 2â†’ * @cipherbox/crypto - ECIES Key Wrapping (Encryption)
 3â†’ *
 4â†’ * Wraps symmetric keys using ECIES with secp256k1.
 5â†’ * Uses eciesjs library which handles ephemeral keys, ECDH, HKDF, and AES-GCM internally.
 6â†’ */
 7â†’
 8â†’import { encrypt } from 'eciesjs';
 9â†’import { ProjectivePoint } from '@noble/secp256k1';
10â†’import { CryptoError } from '../types';
11â†’import { SECP256K1_PUBLIC_KEY_SIZE } from '../constants';
12â†’
13â†’/**
14â†’ * Wrap a key using ECIES encryption.
15â†’ *
16â†’ * Encrypts a symmetric key (or any data) to a recipient's public key.
17â†’ * Only the holder of the corresponding private key can unwrap it.
18â†’ *
19â†’ * Each call produces different ciphertext due to ephemeral key generation.
20â†’ *
21â†’ * @param key - Data to wrap (typically a 32-byte file key)
22â†’ * @param recipientPublicKey - 65-byte uncompressed secp256k1 public key
23â†’ * @returns Wrapped key ciphertext
24â†’ * @throws CryptoError with generic message on any failure
25â†’ */
26â†’export async function wrapKey(
27â†’  key: Uint8Array,
28â†’  recipientPublicKey: Uint8Array
29â†’): Promise<Uint8Array> {
30â†’  // Validate public key size (uncompressed secp256k1)
31â†’  if (recipientPublicKey.length !== SECP256K1_PUBLIC_KEY_SIZE) {
32â†’    throw new CryptoError('Key wrapping failed', 'INVALID_PUBLIC_KEY_SIZE');
33â†’  }
34â†’
35â†’  // Validate uncompressed public key prefix (0x04)
36â†’  if (recipientPublicKey[0] !== 0x04) {
37â†’    throw new CryptoError('Key wrapping failed', 'INVALID_PUBLIC_KEY_FORMAT');
38â†’  }
39â†’
40â†’  // Validate that the public key is a valid point on secp256k1 curve
41â†’  try {
42â†’    ProjectivePoint.fromHex(recipientPublicKey);
43â†’  } catch {
44â†’    throw new CryptoError('Key wrapping failed', 'INVALID_PUBLIC_KEY_FORMAT');
45â†’  }
46â†’
47â†’  try {
48â†’    // eciesjs encrypt handles:
49â†’    // 1. Generate ephemeral keypair
50â†’    // 2. ECDH key agreement
51â†’    // 3. HKDF key derivation
52â†’    // 4. AES-GCM encryption
53â†’    // 5. Return: ephemeral_pubkey || ciphertext || tag
54â†’    const wrapped = encrypt(recipientPublicKey, key);
55â†’    return wrapped;
56â†’  } catch {
57â†’    // Generic error to prevent information leakage
58â†’    throw new CryptoError('Key wrapping failed', 'KEY_WRAPPING_FAILED');
59â†’  }
60â†’}
61â†’

 1â†’/**
 2â†’ * @cipherbox/crypto - ECIES Key Unwrapping (Decryption)
 3â†’ *
 4â†’ * Unwraps symmetric keys using ECIES with secp256k1.
 5â†’ * Uses eciesjs library which handles ECDH, HKDF, and AES-GCM internally.
 6â†’ */
 7â†’
 8â†’import { decrypt } from 'eciesjs';
 9â†’import { CryptoError } from '../types';
10â†’import { SECP256K1_PRIVATE_KEY_SIZE, ECIES_MIN_CIPHERTEXT_SIZE } from '../constants';
11â†’
12â†’/**
13â†’ * Unwrap a key using ECIES decryption.
14â†’ *
15â†’ * Decrypts a wrapped key using the recipient's private key.
16â†’ * Only succeeds if the key was wrapped with the corresponding public key.
17â†’ *
18â†’ * @param wrappedKey - ECIES ciphertext from wrapKey
19â†’ * @param privateKey - 32-byte secp256k1 private key
20â†’ * @returns Original unwrapped key
21â†’ * @throws CryptoError with generic message on any failure
22â†’ */
23â†’export async function unwrapKey(
24â†’  wrappedKey: Uint8Array,
25â†’  privateKey: Uint8Array
26â†’): Promise<Uint8Array> {
27â†’  // Validate private key size
28â†’  if (privateKey.length !== SECP256K1_PRIVATE_KEY_SIZE) {
29â†’    throw new CryptoError('Key unwrapping failed', 'INVALID_PRIVATE_KEY_SIZE');
30â†’  }
31â†’
32â†’  // Validate minimum ciphertext size (ephemeral pubkey + auth tag)
33â†’  if (wrappedKey.length < ECIES_MIN_CIPHERTEXT_SIZE) {
34â†’    throw new CryptoError('Key unwrapping failed', 'KEY_UNWRAPPING_FAILED');
35â†’  }
36â†’
37â†’  try {
38â†’    // eciesjs decrypt handles:
39â†’    // 1. Extract ephemeral public key from ciphertext
40â†’    // 2. ECDH key agreement with private key
41â†’    // 3. HKDF key derivation
42â†’    // 4. AES-GCM decryption with auth tag verification
43â†’    const unwrapped = decrypt(privateKey, wrappedKey);
44â†’    // Convert Buffer to Uint8Array for consistent API
45â†’    return new Uint8Array(unwrapped);
46â†’  } catch {
47â†’    // Generic error to prevent oracle attacks
48â†’    // Do NOT reveal whether:
49â†’    // - Private key was wrong
50â†’    // - Ciphertext was modified
51â†’    // - Auth tag failed
52â†’    throw new CryptoError('Key unwrapping failed', 'KEY_UNWRAPPING_FAILED');
53â†’  }
54â†’}
55â†’

  1â†’/**
  2â†’ * @cipherbox/crypto - ECIES Tests
  3â†’ *
  4â†’ * Tests for asymmetric key wrapping/unwrapping.
  5â†’ */
  6â†’
  7â†’import { describe, it, expect } from 'vitest';
  8â†’import * as secp256k1 from '@noble/secp256k1';
  9â†’import { wrapKey, unwrapKey } from '../ecies';
 10â†’import { generateFileKey, generateRandomBytes } from '../utils';
 11â†’import {
 12â†’  SECP256K1_PUBLIC_KEY_SIZE,
 13â†’  SECP256K1_PRIVATE_KEY_SIZE,
 14â†’  ECIES_MIN_CIPHERTEXT_SIZE,
 15â†’} from '../constants';
 16â†’
 17â†’/**
 18â†’ * Generate a secp256k1 keypair for testing.
 19â†’ */
 20â†’function generateTestKeypair(): { publicKey: Uint8Array; privateKey: Uint8Array } {
 21â†’  const privateKey = secp256k1.utils.randomPrivateKey();
 22â†’  const publicKey = secp256k1.getPublicKey(privateKey, false); // uncompressed
 23â†’  return { publicKey, privateKey };
 24â†’}
 25â†’
 26â†’describe('ECIES Key Wrapping', () => {
 27â†’  describe('wrapKey / unwrapKey round-trip', () => {
 28â†’    it('should wrap and unwrap a 32-byte key correctly', async () => {
 29â†’      const keypair = generateTestKeypair();
 30â†’      const originalKey = generateFileKey();
 31â†’
 32â†’      const wrappedKey = await wrapKey(originalKey, keypair.publicKey);
 33â†’      const unwrappedKey = await unwrapKey(wrappedKey, keypair.privateKey);
 34â†’
 35â†’      expect(unwrappedKey).toEqual(originalKey);
 36â†’    });
 37â†’
 38â†’    it('should handle small data (1 byte)', async () => {
 39â†’      const keypair = generateTestKeypair();
 40â†’      const originalData = new Uint8Array([0x42]);
 41â†’
 42â†’      const wrapped = await wrapKey(originalData, keypair.publicKey);
 43â†’      const unwrapped = await unwrapKey(wrapped, keypair.privateKey);
 44â†’
 45â†’      expect(unwrapped).toEqual(originalData);
 46â†’    });
 47â†’
 48â†’    it('should handle arbitrary size data', async () => {
 49â†’      const keypair = generateTestKeypair();
 50â†’      const originalData = new TextEncoder().encode('This is some arbitrary data for testing');
 51â†’
 52â†’      const wrapped = await wrapKey(originalData, keypair.publicKey);
 53â†’      const unwrapped = await unwrapKey(wrapped, keypair.privateKey);
 54â†’
 55â†’      expect(new TextDecoder().decode(unwrapped)).toBe('This is some arbitrary data for testing');
 56â†’    });
 57â†’
 58â†’    it('should produce wrapped output larger than input (includes ephemeral key)', async () => {
 59â†’      const keypair = generateTestKeypair();
 60â†’      const originalKey = generateFileKey(); // 32 bytes
 61â†’
 62â†’      const wrapped = await wrapKey(originalKey, keypair.publicKey);
 63â†’
 64â†’      // ECIES output includes:
 65â†’      // - 65 bytes ephemeral public key (uncompressed)
 66â†’      // - Ciphertext (same length as plaintext)
 67â†’      // - 16 bytes auth tag
 68â†’      // Total: 65 + 32 + 16 = 113 bytes minimum
 69â†’      expect(wrapped.length).toBeGreaterThan(originalKey.length);
 70â†’    });
 71â†’  });
 72â†’
 73â†’  describe('unwrap with wrong private key', () => {
 74â†’    it('should throw generic error with wrong private key', async () => {
 75â†’      const keypair1 = generateTestKeypair();
 76â†’      const keypair2 = generateTestKeypair();
 77â†’      const originalKey = generateFileKey();
 78â†’
 79â†’      const wrapped = await wrapKey(originalKey, keypair1.publicKey);
 80â†’
 81â†’      await expect(unwrapKey(wrapped, keypair2.privateKey)).rejects.toThrow(
 82â†’        'Key unwrapping failed'
 83â†’      );
 84â†’    });
 85â†’  });
 86â†’
 87â†’  describe('public key length validation', () => {
 88â†’    it('should reject non-65-byte public keys (too short)', async () => {
 89â†’      const shortKey = generateRandomBytes(33); // compressed key size
 90â†’      const data = generateFileKey();
 91â†’
 92â†’      await expect(wrapKey(data, shortKey)).rejects.toThrow('Key wrapping failed');
 93â†’    });
 94â†’
 95â†’    it('should reject non-65-byte public keys (too long)', async () => {
 96â†’      const longKey = generateRandomBytes(100);
 97â†’      const data = generateFileKey();
 98â†’
 99â†’      await expect(wrapKey(data, longKey)).rejects.toThrow('Key wrapping failed');
100â†’    });
101â†’
102â†’    it('should accept exactly 65-byte uncompressed public key', async () => {
103â†’      const keypair = generateTestKeypair();
104â†’      expect(keypair.publicKey.length).toBe(SECP256K1_PUBLIC_KEY_SIZE);
105â†’
106â†’      const data = generateFileKey();
107â†’      // Should not throw
108â†’      const wrapped = await wrapKey(data, keypair.publicKey);
109â†’      expect(wrapped.length).toBeGreaterThan(0);
110â†’    });
111â†’
112â†’    it('should reject public key without 0x04 prefix', async () => {
113â†’      const keypair = generateTestKeypair();
114â†’      // Create a 65-byte array with wrong prefix
115â†’      const badPublicKey = new Uint8Array(keypair.publicKey);
116â†’      badPublicKey[0] = 0x02; // Change prefix to compressed format marker
117â†’
118â†’      await expect(wrapKey(generateFileKey(), badPublicKey)).rejects.toThrow('Key wrapping failed');
119â†’    });
120â†’  });
121â†’
122â†’  describe('private key length validation', () => {
123â†’    it('should reject non-32-byte private keys for unwrapping', async () => {
124â†’      const keypair = generateTestKeypair();
125â†’      const data = generateFileKey();
126â†’      const wrapped = await wrapKey(data, keypair.publicKey);
127â†’
128â†’      const shortPrivateKey = generateRandomBytes(16);
129â†’      await expect(unwrapKey(wrapped, shortPrivateKey)).rejects.toThrow('Key unwrapping failed');
130â†’    });
131â†’
132â†’    it('should accept exactly 32-byte private key', async () => {
133â†’      const keypair = generateTestKeypair();
134â†’      expect(keypair.privateKey.length).toBe(SECP256K1_PRIVATE_KEY_SIZE);
135â†’
136â†’      const data = generateFileKey();
137â†’      const wrapped = await wrapKey(data, keypair.publicKey);
138â†’
139â†’      // Should not throw
140â†’      const unwrapped = await unwrapKey(wrapped, keypair.privateKey);
141â†’      expect(unwrapped).toEqual(data);
142â†’    });
143â†’  });
144â†’
145â†’  describe('ephemeral key randomness', () => {
146â†’    it('should produce different ciphertext for same key wrapped multiple times', async () => {
147â†’      const keypair = generateTestKeypair();
148â†’      const originalKey = generateFileKey();
149â†’
150â†’      const wrapped1 = await wrapKey(originalKey, keypair.publicKey);
151â†’      const wrapped2 = await wrapKey(originalKey, keypair.publicKey);
152â†’      const wrapped3 = await wrapKey(originalKey, keypair.publicKey);
153â†’
154â†’      // All wrappings should be different due to ephemeral key
155â†’      expect(wrapped1).not.toEqual(wrapped2);
156â†’      expect(wrapped2).not.toEqual(wrapped3);
157â†’      expect(wrapped1).not.toEqual(wrapped3);
158â†’
159â†’      // But all should unwrap to same original
160â†’      const unwrapped1 = await unwrapKey(wrapped1, keypair.privateKey);
161â†’      const unwrapped2 = await unwrapKey(wrapped2, keypair.privateKey);
162â†’      const unwrapped3 = await unwrapKey(wrapped3, keypair.privateKey);
163â†’
164â†’      expect(unwrapped1).toEqual(originalKey);
165â†’      expect(unwrapped2).toEqual(originalKey);
166â†’      expect(unwrapped3).toEqual(originalKey);
167â†’    });
168â†’  });
169â†’
170â†’  describe('ciphertext tampering', () => {
171â†’    it('should throw on tampered wrapped key', async () => {
172â†’      const keypair = generateTestKeypair();
173â†’      const originalKey = generateFileKey();
174â†’
175â†’      const wrapped = await wrapKey(originalKey, keypair.publicKey);
176â†’
177â†’      // Tamper with a byte in the middle
178â†’      const tampered = new Uint8Array(wrapped);
179â†’      tampered[Math.floor(tampered.length / 2)] ^= 0xff;
180â†’
181â†’      await expect(unwrapKey(tampered, keypair.privateKey)).rejects.toThrow(
182â†’        'Key unwrapping failed'
183â†’      );
184â†’    });
185â†’
186â†’    it('should throw on truncated wrapped key', async () => {
187â†’      const keypair = generateTestKeypair();
188â†’      const originalKey = generateFileKey();
189â†’
190â†’      const wrapped = await wrapKey(originalKey, keypair.publicKey);
191â†’
192â†’      // Truncate the wrapped data
193â†’      const truncated = wrapped.slice(0, wrapped.length - 10);
194â†’
195â†’      await expect(unwrapKey(truncated, keypair.privateKey)).rejects.toThrow(
196â†’        'Key unwrapping failed'
197â†’      );
198â†’    });
199â†’  });
200â†’
201â†’  describe('error message security', () => {
202â†’    it('should use generic error messages for all failures', async () => {
203â†’      const keypair = generateTestKeypair();
204â†’      const originalKey = generateFileKey();
205â†’      const wrapped = await wrapKey(originalKey, keypair.publicKey);
206â†’
207â†’      const wrongKeypair = generateTestKeypair();
208â†’      const tampered = new Uint8Array(wrapped);
209â†’      tampered[0] ^= 0xff;
210â†’
211â†’      const errors: string[] = [];
212â†’
213â†’      // Wrong private key
214â†’      try {
215â†’        await unwrapKey(wrapped, wrongKeypair.privateKey);
216â†’      } catch (e) {
217â†’        errors.push((e as Error).message);
218â†’      }
219â†’
220â†’      // Tampered ciphertext
221â†’      try {
222â†’        await unwrapKey(tampered, keypair.privateKey);
223â†’      } catch (e) {
224â†’        errors.push((e as Error).message);
225â†’      }
226â†’
227â†’      // All errors should be identical to prevent oracle attacks
228â†’      expect(new Set(errors).size).toBe(1);
229â†’      expect(errors[0]).toBe('Key unwrapping failed');
230â†’    });
231â†’  });
232â†’
233â†’  describe('minimum ciphertext size validation', () => {
234â†’    it('should reject wrapped key shorter than minimum ECIES size (81 bytes)', async () => {
235â†’      const keypair = generateTestKeypair();
236â†’      const shortWrapped = new Uint8Array(ECIES_MIN_CIPHERTEXT_SIZE - 1);
237â†’
238â†’      await expect(unwrapKey(shortWrapped, keypair.privateKey)).rejects.toThrow(
239â†’        'Key unwrapping failed'
240â†’      );
241â†’    });
242â†’
243â†’    it('should accept wrapped key at or above minimum size (empty plaintext)', async () => {
244â†’      const keypair = generateTestKeypair();
245â†’      const emptyData = new Uint8Array(0);
246â†’
247â†’      const wrapped = await wrapKey(emptyData, keypair.publicKey);
248â†’      // eciesjs adds additional overhead beyond minimum (ephemeral key + HKDF + tag)
249â†’      expect(wrapped.length).toBeGreaterThanOrEqual(ECIES_MIN_CIPHERTEXT_SIZE);
250â†’
251â†’      const unwrapped = await unwrapKey(wrapped, keypair.privateKey);
252â†’      expect(unwrapped.length).toBe(0);
253â†’    });
254â†’  });
255â†’
256â†’  describe('curve point validation', () => {
257â†’    it('should reject public key with valid length but invalid curve point', async () => {
258â†’      // Create a 65-byte key with 0x04 prefix but invalid point coordinates
259â†’      const invalidPublicKey = new Uint8Array(65);
260â†’      invalidPublicKey[0] = 0x04;
261â†’      // Fill with 0xff - this is not a valid point on secp256k1
262â†’      invalidPublicKey.fill(0xff, 1);
263â†’
264â†’      const data = generateFileKey();
265â†’      await expect(wrapKey(data, invalidPublicKey)).rejects.toThrow('Key wrapping failed');
266â†’    });
267â†’
268â†’    it('should reject public key with coordinates that pass length/prefix but fail curve check', async () => {
269â†’      // Create a seemingly valid public key that's not on the curve
270â†’      const invalidPublicKey = new Uint8Array(65);
271â†’      invalidPublicKey[0] = 0x04;
272â†’      // Set X coordinate to some value
273â†’      for (let i = 1; i <= 32; i++) {
274â†’        invalidPublicKey[i] = i;
275â†’      }
276â†’      // Set Y coordinate to some value (likely not a valid Y for this X)
277â†’      for (let i = 33; i <= 64; i++) {
278â†’        invalidPublicKey[i] = i;
279â†’      }
280â†’
281â†’      const data = generateFileKey();
282â†’      await expect(wrapKey(data, invalidPublicKey)).rejects.toThrow('Key wrapping failed');
283â†’    });
284â†’
285â†’    it('should accept valid public key on the curve', async () => {
286â†’      const keypair = generateTestKeypair();
287â†’      const data = generateFileKey();
288â†’
289â†’      // Should not throw
290â†’      const wrapped = await wrapKey(data, keypair.publicKey);
291â†’      expect(wrapped.length).toBeGreaterThan(0);
292â†’    });
293â†’  });
294â†’
295â†’  describe('empty data handling', () => {
296â†’    it('should handle empty data wrapping correctly', async () => {
297â†’      const keypair = generateTestKeypair();
298â†’      const emptyData = new Uint8Array(0);
299â†’
300â†’      const wrapped = await wrapKey(emptyData, keypair.publicKey);
301â†’      const unwrapped = await unwrapKey(wrapped, keypair.privateKey);
302â†’
303â†’      expect(unwrapped.length).toBe(0);
304â†’    });
305â†’  });
306â†’});
