 1â†’/**
 2â†’ * @cipherbox/crypto - Type Definitions
 3â†’ *
 4â†’ * Core types for cryptographic operations.
 5â†’ */
 6â†’
 7â†’/**
 8â†’ * User's vault keypair for ECIES operations.
 9â†’ * Source agnostic - same type whether derived from Web3Auth or external wallet (ADR-001).
10â†’ */
11â†’export type VaultKey = {
12â†’  /** 65-byte uncompressed secp256k1 public key */
13â†’  publicKey: Uint8Array;
14â†’  /** 32-byte secp256k1 private key */
15â†’  privateKey: Uint8Array;
16â†’};
17â†’
18â†’/**
19â†’ * Result of AES-GCM encryption.
20â†’ * IV and ciphertext are kept separate for explicit handling.
21â†’ */
22â†’export type EncryptedData = {
23â†’  /** Encrypted data including authentication tag */
24â†’  ciphertext: Uint8Array;
25â†’  /** 12-byte initialization vector */
26â†’  iv: Uint8Array;
27â†’};
28â†’
29â†’/**
30â†’ * Error codes for categorized error handling.
31â†’ * Generic messages are still used externally to prevent oracle attacks.
32â†’ */
33â†’export type CryptoErrorCode =
34â†’  | 'ENCRYPTION_FAILED'
35â†’  | 'DECRYPTION_FAILED'
36â†’  | 'KEY_WRAPPING_FAILED'
37â†’  | 'KEY_UNWRAPPING_FAILED'
38â†’  | 'INVALID_KEY_SIZE'
39â†’  | 'INVALID_IV_SIZE'
40â†’  | 'INVALID_PUBLIC_KEY_SIZE'
41â†’  | 'INVALID_PUBLIC_KEY_FORMAT'
42â†’  | 'INVALID_PRIVATE_KEY_SIZE'
43â†’  | 'INVALID_INPUT'
44â†’  | 'INVALID_SIGNATURE_SIZE'
45â†’  | 'SIGNING_FAILED'
46â†’  | 'RANDOM_GENERATION_FAILED'
47â†’  | 'SECURE_CONTEXT_REQUIRED';
48â†’
49â†’/**
50â†’ * Custom error class for cryptographic operations.
51â†’ * Provides error codes for internal handling while keeping messages generic.
52â†’ */
53â†’export class CryptoError extends Error {
54â†’  readonly code: CryptoErrorCode;
55â†’
56â†’  constructor(message: string, code: CryptoErrorCode) {
57â†’    super(message);
58â†’    this.name = 'CryptoError';
59â†’    this.code = code;
60â†’
61â†’    // Maintain proper stack trace for V8 (Node.js-specific)
62â†’    const ErrorWithCapture = Error as typeof Error & {
63â†’      captureStackTrace?: (target: object, constructor: unknown) => void;
64â†’    };
65â†’    if (ErrorWithCapture.captureStackTrace) {
66â†’      ErrorWithCapture.captureStackTrace(this, CryptoError);
67â†’    }
68â†’  }
69â†’}
