/**
 * @cipherbox/crypto - ECIES Re-Wrapping Tests
 *
 * Tests for key re-wrapping used in user-to-user sharing.
 */

import { describe, it, expect } from 'vitest';
import * as secp256k1 from '@noble/secp256k1';
import { wrapKey, unwrapKey, reWrapKey } from '../ecies';
import { generateRandomBytes } from '../utils';
import { CryptoError } from '../types';

/**
 * Generate a secp256k1 keypair for testing.
 */
function generateTestKeypair(): { publicKey: Uint8Array; privateKey: Uint8Array } {
  const privateKey = secp256k1.utils.randomPrivateKey();
  const publicKey = secp256k1.getPublicKey(privateKey, false); // uncompressed
  return { publicKey, privateKey };
}

describe('ECIES Key Re-Wrapping', () => {
  it('re-wrapped key decrypts to same value', async () => {
    const alice = generateTestKeypair();
    const bob = generateTestKeypair();

    // Create a random 32-byte key and wrap it for Alice
    const originalKey = generateRandomBytes(32);
    const wrappedForAlice = await wrapKey(originalKey, alice.publicKey);

    // Re-wrap from Alice to Bob
    const wrappedForBob = await reWrapKey(
      wrappedForAlice,
      alice.privateKey,
      bob.publicKey
    );

    // Bob unwraps with his private key
    const bobsKey = await unwrapKey(wrappedForBob, bob.privateKey);

    // Both should get the same original key
    expect(bobsKey).toEqual(originalKey);
  });

  it('re-wrapping preserves key across multiple recipients', async () => {
    const alice = generateTestKeypair();
    const bob = generateTestKeypair();
    const charlie = generateTestKeypair();

    // Create a random 32-byte key and wrap it for Alice
    const originalKey = generateRandomBytes(32);
    const wrappedForAlice = await wrapKey(originalKey, alice.publicKey);

    // Re-wrap from Alice to Bob
    const wrappedForBob = await reWrapKey(
      wrappedForAlice,
      alice.privateKey,
      bob.publicKey
    );

    // Re-wrap from Alice to Charlie
    const wrappedForCharlie = await reWrapKey(
      wrappedForAlice,
      alice.privateKey,
      charlie.publicKey
    );

    // All three should unwrap to the same key
    const aliceKey = await unwrapKey(wrappedForAlice, alice.privateKey);
    const bobKey = await unwrapKey(wrappedForBob, bob.privateKey);
    const charlieKey = await unwrapKey(wrappedForCharlie, charlie.privateKey);

    expect(aliceKey).toEqual(originalKey);
    expect(bobKey).toEqual(originalKey);
    expect(charlieKey).toEqual(originalKey);
  });

  it('reWrapKey fails with wrong owner private key', async () => {
    const alice = generateTestKeypair();
    const bob = generateTestKeypair();
    const wrongKeypair = generateTestKeypair();

    // Wrap a key for Alice
    const originalKey = generateRandomBytes(32);
    const wrappedForAlice = await wrapKey(originalKey, alice.publicKey);

    // Try to re-wrap with wrong private key
    await expect(
      reWrapKey(wrappedForAlice, wrongKeypair.privateKey, bob.publicKey)
    ).rejects.toThrow(CryptoError);

    await expect(
      reWrapKey(wrappedForAlice, wrongKeypair.privateKey, bob.publicKey)
    ).rejects.toThrow('Key re-wrapping failed');
  });

  it('reWrapKey fails with invalid recipient public key', async () => {
    const alice = generateTestKeypair();

    // Wrap a key for Alice
    const originalKey = generateRandomBytes(32);
    const wrappedForAlice = await wrapKey(originalKey, alice.publicKey);

    // Create an invalid 65-byte public key (valid length, invalid curve point)
    const invalidPublicKey = new Uint8Array(65);
    invalidPublicKey[0] = 0x04;
    invalidPublicKey.fill(0xff, 1);

    // Try to re-wrap with invalid recipient public key
    await expect(
      reWrapKey(wrappedForAlice, alice.privateKey, invalidPublicKey)
    ).rejects.toThrow(CryptoError);

    await expect(
      reWrapKey(wrappedForAlice, alice.privateKey, invalidPublicKey)
    ).rejects.toThrow('Key re-wrapping failed');
  });
});

import {
  Entity,
  PrimaryGeneratedColumn,
  Column,
  CreateDateColumn,
  UpdateDateColumn,
  ManyToOne,
  OneToMany,
  JoinColumn,
  Index,
  Unique,
} from 'typeorm';
import { User } from '../../auth/entities/user.entity';
import { ShareKey } from './share-key.entity';

@Entity('shares')
@Unique(['sharerId', 'recipientId', 'ipnsName'])
export class Share {
  @PrimaryGeneratedColumn('uuid')
  id!: string;

  @Index()
  @Column({ type: 'uuid', name: 'sharer_id' })
  sharerId!: string;

  @ManyToOne(() => User, { onDelete: 'CASCADE' })
  @JoinColumn({ name: 'sharer_id' })
  sharer!: User;

  @Index()
  @Column({ type: 'uuid', name: 'recipient_id' })
  recipientId!: string;

  @ManyToOne(() => User, { onDelete: 'CASCADE' })
  @JoinColumn({ name: 'recipient_id' })
  recipient!: User;

  @Column({ type: 'varchar', length: 10, name: 'item_type' })
  itemType!: 'folder' | 'file';

  @Index()
  @Column({ type: 'varchar', length: 255, name: 'ipns_name' })
  ipnsName!: string;

  /**
   * Display name of the shared item (plaintext).
   * Privacy impact is minimal -- server already knows user IDs involved.
   */
  @Column({ type: 'varchar', length: 255, name: 'item_name' })
  itemName!: string;

  /**
   * The shared item's key (folderKey or parent folderKey) wrapped
   * with the recipient's secp256k1 public key via ECIES.
   * Server never sees the plaintext key.
   */
  @Column({ type: 'bytea', name: 'encrypted_key' })
  encryptedKey!: Buffer;

  /**
   * Recipient has hidden/dismissed this share from their view.
   */
  @Column({ type: 'boolean', name: 'hidden_by_recipient', default: false })
  hiddenByRecipient!: boolean;

  /**
   * Soft-delete timestamp for lazy key rotation.
   * null = active share, timestamp = revoked pending rotation.
   * On next folder modification, sharer rotates folderKey and hard-deletes.
   */
  @Column({ type: 'timestamp', name: 'revoked_at', nullable: true })
  revokedAt!: Date | null;

  @OneToMany(() => ShareKey, (shareKey) => shareKey.share, { cascade: true })
  shareKeys!: ShareKey[];

  @CreateDateColumn({ name: 'created_at' })
  createdAt!: Date;

  @UpdateDateColumn({ name: 'updated_at' })
  updatedAt!: Date;
}

import {
  Entity,
  PrimaryGeneratedColumn,
  Column,
  CreateDateColumn,
  ManyToOne,
  JoinColumn,
  Index,
  Unique,
} from 'typeorm';
import { Share } from './share.entity';

@Entity('share_keys')
@Unique(['shareId', 'keyType', 'itemId'])
export class ShareKey {
  @PrimaryGeneratedColumn('uuid')
  id!: string;

  @Index()
  @Column({ type: 'uuid', name: 'share_id' })
  shareId!: string;

  @ManyToOne(() => Share, (share) => share.shareKeys, { onDelete: 'CASCADE' })
  @JoinColumn({ name: 'share_id' })
  share!: Share;

  /**
   * Type of key stored: 'file' for fileKey, 'folder' for subfolder folderKey.
   */
  @Column({ type: 'varchar', length: 10, name: 'key_type' })
  keyType!: 'file' | 'folder';

  /**
   * UUID of the file or subfolder this key belongs to.
   */
  @Index()
  @Column({ type: 'varchar', length: 255, name: 'item_id' })
  itemId!: string;

  /**
   * The file key or subfolder key wrapped with the recipient's
   * secp256k1 public key via ECIES. Server never sees the plaintext key.
   */
  @Column({ type: 'bytea', name: 'encrypted_key' })
  encryptedKey!: Buffer;

  @CreateDateColumn({ name: 'created_at' })
  createdAt!: Date;
}

