 26â†’    const pendingCount = await this.repo.count({
 27â†’      where: {
 28â†’        userId,
 29â†’        status: 'pending' as const,
 30â†’        expiresAt: MoreThan(new Date()),
 31â†’      },
 32â†’    });
 33â†’
 34â†’    if (pendingCount >= MAX_PENDING_REQUESTS) {
 35â†’      throw new BadRequestException(
 36â†’        `Maximum of ${MAX_PENDING_REQUESTS} pending approval requests allowed`
 37â†’      );
 38â†’    }
 39â†’
 40â†’    const approval = this.repo.create({
 41â†’      userId,
 42â†’      deviceId: dto.deviceId,
 43â†’      deviceName: dto.deviceName,
 44â†’      ephemeralPublicKey: dto.ephemeralPublicKey,
 45â†’      status: 'pending',
 46â†’      expiresAt: new Date(Date.now() + TTL_MS),
 47â†’    });
 48â†’
 49â†’    const saved = await this.repo.save(approval);
 50â†’    return { requestId: saved.id };
 51â†’  }
 52â†’
 53â†’  /**
 54â†’   * Get the status of an approval request.
 55â†’   * Auto-expires pending requests that have passed their TTL.
 56â†’   */
 57â†’  async getStatus(
 58â†’    requestId: string,
 59â†’    userId: string
 60â†’  ): Promise<{
 61â†’    status: string;
 62â†’    encryptedFactorKey?: string;
 63â†’  }> {
