 67â†’
 68â†’    if (!approval) {
 69â†’      throw new NotFoundException('Approval request not found');
 70â†’    }
 71â†’
 72â†’    // Auto-expire pending requests past TTL
 73â†’    if (approval.status === 'pending' && approval.expiresAt < new Date()) {
 74â†’      approval.status = 'expired';
 75â†’      await this.repo.save(approval);
 76â†’    }
 77â†’
 78â†’    return {
 79â†’      status: approval.status,
 80â†’      ...(approval.encryptedFactorKey ? { encryptedFactorKey: approval.encryptedFactorKey } : {}),
 81â†’    };
