108â†’      deviceId: a.deviceId,
109â†’      deviceName: a.deviceName,
110â†’      ephemeralPublicKey: a.ephemeralPublicKey,
111â†’      createdAt: a.createdAt,
112â†’      expiresAt: a.expiresAt,
113â†’    }));
114â†’  }
115â†’
116â†’  /**
117â†’   * Respond to an approval request (approve or deny).
118â†’   * On approve, stores the ECIES-encrypted factor key.
119â†’   */
120â†’  async respond(requestId: string, userId: string, dto: RespondApprovalDto): Promise<void> {
121â†’    await this.repo.manager.transaction(async (manager) => {
122â†’      const approval = await manager.findOne(DeviceApproval, {
123â†’        where: { id: requestId, userId },
124â†’        lock: { mode: 'pessimistic_write' },
125â†’      });
126â†’
127â†’      if (!approval) {
128â†’        throw new NotFoundException('Approval request not found');
129â†’      }
130â†’
131â†’      if (approval.status !== 'pending') {
132â†’        throw new BadRequestException('Approval request has already been responded to');
133â†’      }
134â†’
135â†’      if (approval.expiresAt < new Date()) {
