137â†’        await manager.save(approval);
138â†’        throw new BadRequestException('Approval request has expired');
139â†’      }
140â†’
141â†’      // H-02: Prevent self-approval â€” responding device must differ from requesting device
142â†’      if (dto.respondedByDeviceId === approval.deviceId) {
143â†’        throw new BadRequestException('A device cannot approve its own request');
144â†’      }
145â†’
146â†’      if (dto.action === 'approve') {
147â†’        // H-03: Require encryptedFactorKey when approving (defense-in-depth)
148â†’        if (!dto.encryptedFactorKey) {
149â†’          throw new BadRequestException('encryptedFactorKey is required when approving');
150â†’        }
151â†’        approval.encryptedFactorKey = dto.encryptedFactorKey;
152â†’      }
153â†’
154â†’      approval.status = dto.action === 'approve' ? 'approved' : 'denied';
155â†’      approval.respondedBy = dto.respondedByDeviceId;
156â†’      await manager.save(approval);
157â†’    });
158â†’  }
159â†’
160â†’  /**
161â†’   * Cancel a pending approval request.
162â†’   * Only works for pending requests owned by the calling user.
163â†’   */
164â†’  async cancel(requestId: string, userId: string): Promise<void> {
165â†’    const approval = await this.repo.findOne({
166â†’      where: { id: requestId, userId },
167â†’    });
168â†’
169â†’    if (!approval) {
170â†’      throw new NotFoundException('Approval request not found');
171â†’    }
172â†’
173â†’    if (approval.status !== 'pending') {
174â†’      throw new NotFoundException('Only pending requests can be cancelled');
175â†’    }
176â†’
177â†’    await this.repo.remove(approval);
178â†’  }
179â†’}
