108â†’  async getSentShares(sharerId: string): Promise<Share[]> {
109â†’    return this.shareRepo.find({
110â†’      where: {
111â†’        sharerId,
112â†’        revokedAt: IsNull(),
113â†’      },
114â†’      relations: ['recipient'],
115â†’      order: { createdAt: 'DESC' },
116â†’    });
117â†’  }
