124â†’    const share = await this.shareRepo.findOne({ where: { id: shareId } });
125â†’
126â†’    if (!share) {
127â†’      throw new NotFoundException('Share not found');
128â†’    }
129â†’
130â†’    if (share.sharerId !== userId && share.recipientId !== userId) {
131â†’      throw new ForbiddenException('Not authorized to access this share');
132â†’    }
133â†’
134â†’    return this.shareKeyRepo.find({
135â†’      where: { shareId },
136â†’      order: { createdAt: 'ASC' },
137â†’    });
138â†’  }
139â†’
140â†’  /**
141â†’   * Add or update re-wrapped keys for an existing share.
142â†’   * Only the sharer can add keys.
143â†’   */
144â†’  async addShareKeys(shareId: string, sharerId: string, dto: AddShareKeysDto): Promise<void> {
145â†’    const share = await this.shareRepo.findOne({ where: { id: shareId } });
146â†’
147â†’    if (!share) {
148â†’      throw new NotFoundException('Share not found');
149â†’    }
150â†’
151â†’    if (share.sharerId !== sharerId) {
152â†’      throw new ForbiddenException('Only the sharer can add keys');
153â†’    }
154â†’
155â†’    // Upsert: insert or update encrypted_key for each itemId
156â†’    for (const entry of dto.keys) {
157â†’      const existing = await this.shareKeyRepo.findOne({
158â†’        where: {
159â†’          shareId,
160â†’          keyType: entry.keyType,
161â†’          itemId: entry.itemId,
162â†’        },
163â†’      });
164â†’
165â†’      if (existing) {
166â†’        existing.encryptedKey = Buffer.from(entry.encryptedKey, 'hex');
167â†’        await this.shareKeyRepo.save(existing);
168â†’      } else {
169â†’        const shareKey = this.shareKeyRepo.create({
170â†’          shareId,
171â†’          keyType: entry.keyType,
172â†’          itemId: entry.itemId,
173â†’          encryptedKey: Buffer.from(entry.encryptedKey, 'hex'),
174â†’        });
175â†’        await this.shareKeyRepo.save(shareKey);
176â†’      }
177â†’    }
178â†’  }
179â†’
180â†’  /**
181â†’   * Soft-delete a share by setting revokedAt.
182â†’   * Only the sharer can revoke. ShareKey records are kept for lazy rotation.
183â†’   */
184â†’  async revokeShare(shareId: string, sharerId: string): Promise<void> {
185â†’    const share = await this.shareRepo.findOne({ where: { id: shareId } });
186â†’
187â†’    if (!share) {
188â†’      throw new NotFoundException('Share not found');
189â†’    }
190â†’
191â†’    if (share.sharerId !== sharerId) {
192â†’      throw new ForbiddenException('Only the sharer can revoke a share');
193â†’    }
194â†’
195â†’    share.revokedAt = new Date();
196â†’    await this.shareRepo.save(share);
197â†’  }
198â†’
199â†’  /**
200â†’   * Hide a share from the recipient's view.
201â†’   * Only the recipient can hide a share.
202â†’   */
203â†’  async hideShare(shareId: string, recipientId: string): Promise<void> {
204â†’    const share = await this.shareRepo.findOne({ where: { id: shareId } });
205â†’
206â†’    if (!share) {
207â†’      throw new NotFoundException('Share not found');
208â†’    }
209â†’
210â†’    if (share.recipientId !== recipientId) {
211â†’      throw new ForbiddenException('Only the recipient can hide a share');
212â†’    }
213â†’
214â†’    share.hiddenByRecipient = true;
215â†’    await this.shareRepo.save(share);
216â†’  }
217â†’
218â†’  /**
219â†’   * Look up a user by their secp256k1 public key.
220â†’   * Used to verify recipient exists before sharing.
221â†’   */
222â†’  async lookupUserByPublicKey(
223â†’    publicKey: string
224â†’  ): Promise<{ userId: string; publicKey: string } | null> {
225â†’    const user = await this.userRepo.findOne({
226â†’      where: { publicKey },
227â†’    });
228â†’
229â†’    if (!user) {
230â†’      return null;
231â†’    }
232â†’
233â†’    return { userId: user.id, publicKey: user.publicKey };
234â†’  }
235â†’
236â†’  /**
237â†’   * Get shares pending key rotation (revoked but not yet hard-deleted).
238â†’   */
239â†’  async getPendingRotations(sharerId: string): Promise<Share[]> {
240â†’    return this.shareRepo.find({
241â†’      where: {
242â†’        sharerId,
243â†’        revokedAt: Not(IsNull()),
244â†’      },
245â†’      relations: ['recipient'],
246â†’      order: { revokedAt: 'ASC' },
247â†’    });
248â†’  }
249â†’
250â†’  /**
251â†’   * Hard-delete a share and all associated keys after rotation is complete.
252â†’   */
253â†’  async completeRotation(shareId: string): Promise<void> {
254â†’    const share = await this.shareRepo.findOne({ where: { id: shareId } });
255â†’
256â†’    if (!share) {
257â†’      throw new NotFoundException('Share not found');
258â†’    }
259â†’
260â†’    // CASCADE will remove all associated ShareKey records
261â†’    await this.shareRepo.remove(share);
262â†’  }
263â†’}
