 31â†’import { IpnsController } from '../src/ipns/ipns.controller';
 32â†’import { IpnsService } from '../src/ipns/ipns.service';
 33â†’import { DeviceApprovalController } from '../src/device-approval/device-approval.controller';
 34â†’import { DeviceApprovalService } from '../src/device-approval/device-approval.service';
 35â†’import { MetricsService } from '../src/metrics/metrics.service';
 36â†’import { User } from '../src/auth/entities/user.entity';
 37â†’import { AuthMethod } from '../src/auth/entities/auth-method.entity';
 38â†’import { RefreshToken } from '../src/auth/entities/refresh-token.entity';
 39â†’import { getRepositoryToken } from '@nestjs/typeorm';
 40â†’import { ConfigService } from '@nestjs/config';
 41â†’
 42â†’// Mock providers for OpenAPI generation - these won't be called
 43â†’const mockRepository = {
 44â†’  provide: getRepositoryToken(User),
 45â†’  useValue: {},
 46â†’};
 47â†’
 48â†’const mockAuthMethodRepository = {
 49â†’  provide: getRepositoryToken(AuthMethod),
 50â†’  useValue: {},
 51â†’};
 52â†’
 53â†’const mockRefreshTokenRepository = {
 54â†’  provide: getRepositoryToken(RefreshToken),
 55â†’  useValue: {},
 56â†’};
 57â†’
 58â†’const mockJwtService = {
 59â†’  provide: 'JwtService',
 60â†’  useValue: { sign: () => '' },
 61â†’};
 62â†’
 63â†’const mockConfigService = {
 64â†’  provide: ConfigService,
 65â†’  useValue: { get: () => 'mock-value' },
 66â†’};
 67â†’
 68â†’// Minimal module for OpenAPI generation - no database connection needed
 69â†’@Module({
 70â†’  imports: [
 71â†’    // ThrottlerModule is required because IpnsController uses ThrottlerGuard
 72â†’    ThrottlerModule.forRoot([{ ttl: 60000, limit: 10 }]),
 73â†’  ],
 74â†’  controllers: [
 75â†’    AppController,
 76â†’    AuthController,
 77â†’    IdentityController,
 78â†’    IpfsController,
 79â†’    VaultController,
 80â†’    IpnsController,
 81â†’    DeviceApprovalController,
 82â†’  ],
 83â†’  providers: [
 84â†’    AppService,
 85â†’    {
 86â†’      provide: AuthService,
 87â†’      useValue: {},
 88â†’    },
 89â†’    {
 90â†’      provide: Web3AuthVerifierService,
 91â†’      useValue: {},
 92â†’    },
 93â†’    {
 94â†’      provide: TokenService,
 95â†’      useValue: {},
 96â†’    },
 97â†’    {
 98â†’      provide: JwtIssuerService,
 99â†’      useValue: { getJwksData: () => ({ keys: [] }) },
100â†’    },
101â†’    {
102â†’      provide: GoogleOAuthService,
103â†’      useValue: {},
104â†’    },
105â†’    {
106â†’      provide: EmailOtpService,
107â†’      useValue: {},
108â†’    },
109â†’    {
110â†’      provide: SiweService,
111â†’      useValue: {},
112â†’    },
113â†’    {
114â†’      provide: IPFS_PROVIDER,
115â†’      useValue: {},
116â†’    },
117â†’    {
118â†’      provide: VaultService,
119â†’      useValue: {},
120â†’    },
121â†’    {
122â†’      provide: IpnsService,
123â†’      useValue: {},
124â†’    },
125â†’    {
126â†’      provide: DeviceApprovalService,
127â†’      useValue: {},
128â†’    },
129â†’    {
130â†’      provide: MetricsService,
131â†’      useValue: {},
132â†’    },
133â†’    mockRepository,
134â†’    mockAuthMethodRepository,
135â†’    mockRefreshTokenRepository,
136â†’    mockJwtService,
137â†’    mockConfigService,
138â†’  ],
139â†’})
140â†’class OpenApiGeneratorModule {}
141â†’
142â†’async function generateOpenApiSpec() {
143â†’  // Create app without starting HTTP server
144â†’  const app = await NestFactory.create(OpenApiGeneratorModule, {
145â†’    logger: false,
146â†’  });
147â†’
148â†’  const config = new DocumentBuilder()
149â†’    .setTitle('CipherBox API')
150â†’    .setDescription('Zero-knowledge encrypted cloud storage API')
151â†’    .setVersion('0.1.0')
152â†’    .addBearerAuth()
153â†’    .addTag('Health', 'Health check endpoints')
154â†’    .addTag('Root', 'API root endpoints')
155â†’    .addTag('Auth', 'Authentication endpoints')
156â†’    .addTag('Identity', 'CipherBox identity provider endpoints')
157â†’    .addTag('Vault', 'Vault management endpoints')
158â†’    .addTag('Files', 'File operations endpoints')
159â†’    .addTag('IPFS', 'IPFS relay endpoints')
160â†’    .addTag('IPNS', 'IPNS relay endpoints')
161â†’    .addTag('device-approval', 'Cross-device approval endpoints')
162â†’    .build();
163â†’
164â†’  // Create document from the app
165â†’  const document = SwaggerModule.createDocument(app, config);
166â†’
167â†’  // Manually add health endpoint to spec (since we can't inject the real controller
168â†’  // without TypeORM dependencies)
169â†’  document.paths['/health'] = {
170â†’    get: {
171â†’      operationId: 'HealthController_check',
172â†’      summary: 'Check API and database health',
173â†’      tags: ['Health'],
174â†’      responses: {
175â†’        '200': {
176â†’          description: 'Health check passed',
177â†’          content: {
178â†’            'application/json': {
179â†’              schema: {
180â†’                type: 'object',
181â†’                properties: {
182â†’                  status: { type: 'string', example: 'ok' },
183â†’                  info: {
184â†’                    type: 'object',
185â†’                    properties: {
186â†’                      database: {
187â†’                        type: 'object',
188â†’                        properties: {
189â†’                          status: { type: 'string', example: 'up' },
190â†’                        },
191â†’                      },
192â†’                    },
193â†’                  },
194â†’                },
195â†’              },
196â†’            },
197â†’          },
198â†’        },
199â†’        '503': {
200â†’          description: 'Health check failed',
201â†’        },
202â†’      },
203â†’    },
204â†’  };
205â†’
206â†’  // Ensure output directory exists
207â†’  const outputDir = join(__dirname, '..', '..', '..', 'packages', 'api-client');
208â†’  mkdirSync(outputDir, { recursive: true });
209â†’
210â†’  const outputPath = join(outputDir, 'openapi.json');
211â†’  writeFileSync(outputPath, JSON.stringify(document, null, 2));
212â†’
213â†’  console.log(`OpenAPI spec written to: ${outputPath}`);
214â†’
215â†’  await app.close();
216â†’  process.exit(0);
217â†’}
218â†’
219â†’generateOpenApiSpec().catch((error) => {
220â†’  console.error('Failed to generate OpenAPI spec:', error);
221â†’  process.exit(1);
222â†’});
223â†’

