  1â†’import { useRef, useEffect, useMemo } from 'react';
  2â†’import {
  3â†’  useFloating,
  4â†’  offset,
  5â†’  flip,
  6â†’  shift,
  7â†’  autoUpdate,
  8â†’  type VirtualElement,
  9â†’} from '@floating-ui/react';
 10â†’import type { FolderChild } from '@cipherbox/crypto';
 11â†’import { Portal } from '../ui/Portal';
 12â†’import '../../styles/context-menu.css';
 13â†’
 14â†’type ContextMenuProps = {
 15â†’  /** X position (client coordinates) */
 16â†’  x: number;
 17â†’  /** Y position (client coordinates) */
 18â†’  y: number;
 19â†’  /** The file or folder item */
 20â†’  item: FolderChild;
 21â†’  /** Number of selected items (for multi-selection context) */
 22â†’  selectedCount: number;
 23â†’  /** Callback to close the menu */
 24â†’  onClose: () => void;
 25â†’  /** Callback when download is clicked (files only) */
 26â†’  onDownload?: () => void;
 27â†’  /** Callback when edit is clicked (text files only) */
 28â†’  onEdit?: () => void;
 29â†’  /** Callback when preview is clicked (image, PDF, audio, video files) */
 30â†’  onPreview?: () => void;
 31â†’  /** Callback when rename is clicked */
 32â†’  onRename: () => void;
 33â†’  /** Callback when move is clicked */
 34â†’  onMove?: () => void;
 35â†’  /** Callback when delete is clicked */
 36â†’  onDelete: () => void;
 37â†’  /** Callback when details is clicked */
 38â†’  onDetails: () => void;
 39â†’  /** Callback for batch download (multi-selection) */
 40â†’  onBatchDownload?: () => void;
 41â†’  /** Callback for batch move (multi-selection) */
 42â†’  onBatchMove?: () => void;
 43â†’  /** Callback for batch delete (multi-selection) */
 44â†’  onBatchDelete?: () => void;
 45â†’};
 46â†’
 47â†’/**
 48â†’ * Context menu for file/folder actions.
 49â†’ *
 50â†’ * Uses @floating-ui/react for positioning with edge detection.
 51â†’ * Renders in a portal to avoid z-index and overflow issues.
 52â†’ *
 53â†’ * Actions:
 54â†’ * - Download (files only)
 55â†’ * - Rename
 56â†’ * - Details
 57â†’ * - Delete
 58â†’ */
 59â†’export function ContextMenu({
 60â†’  x,
 61â†’  y,
 62â†’  item,
 63â†’  selectedCount,
 64â†’  onClose,
 65â†’  onDownload,
 66â†’  onEdit,
 67â†’  onPreview,
 68â†’  onRename,
 69â†’  onMove,
 70â†’  onDelete,
 71â†’  onDetails,
 72â†’  onBatchDownload,
 73â†’  onBatchMove,
 74â†’  onBatchDelete,
 75â†’}: ContextMenuProps) {
 76â†’  const menuRef = useRef<HTMLDivElement | null>(null);
 77â†’  const isFile = item.type === 'file';
 78â†’
 79â†’  // Create virtual reference element at click position
 80â†’  const virtualReference = useMemo<VirtualElement>(
 81â†’    () => ({
 82â†’      getBoundingClientRect() {
 83â†’        return {
 84â†’          x,
 85â†’          y,
 86â†’          top: y,
 87â†’          left: x,
 88â†’          bottom: y,
 89â†’          right: x,
 90â†’          width: 0,
 91â†’          height: 0,
 92â†’        };
 93â†’      },
 94â†’    }),
 95â†’    [x, y]
 96â†’  );
 97â†’
 98â†’  const { refs, floatingStyles } = useFloating({
 99â†’    placement: 'bottom-start',
100â†’    middleware: [offset(4), flip({ padding: 8 }), shift({ padding: 8 })],
101â†’    whileElementsMounted: autoUpdate,
102â†’  });
103â†’
104â†’  // Set the virtual reference
105â†’  useEffect(() => {
106â†’    refs.setReference(virtualReference);
107â†’  }, [refs, virtualReference]);
108â†’
109â†’  // Handle click outside to close
110â†’  useEffect(() => {
111â†’    const handleClickOutside = (event: MouseEvent) => {
112â†’      if (menuRef.current && !menuRef.current.contains(event.target as Node)) {
113â†’        onClose();
114â†’      }
115â†’    };
116â†’
117â†’    // Use capture phase to handle before other click handlers
118â†’    document.addEventListener('mousedown', handleClickOutside, true);
119â†’    return () => {
120â†’      document.removeEventListener('mousedown', handleClickOutside, true);
121â†’    };
122â†’  }, [onClose]);
123â†’
124â†’  // Handle escape key
125â†’  useEffect(() => {
126â†’    const handleKeyDown = (event: KeyboardEvent) => {
127â†’      if (event.key === 'Escape') {
128â†’        onClose();
129â†’      }
130â†’    };
131â†’
132â†’    document.addEventListener('keydown', handleKeyDown);
133â†’    return () => {
134â†’      document.removeEventListener('keydown', handleKeyDown);
135â†’    };
136â†’  }, [onClose]);
137â†’
138â†’  // Focus the menu on mount for keyboard accessibility
139â†’  useEffect(() => {
140â†’    const firstButton = menuRef.current?.querySelector('button');
141â†’    firstButton?.focus();
142â†’  }, []);
143â†’
144â†’  const handleDownload = () => {
145â†’    onDownload?.();
146â†’    onClose();
147â†’  };
148â†’
149â†’  const handleEdit = () => {
150â†’    onEdit?.();
151â†’    onClose();
152â†’  };
153â†’
154â†’  const handlePreview = () => {
155â†’    onPreview?.();
156â†’    onClose();
157â†’  };
158â†’
159â†’  const handleRename = () => {
160â†’    onRename();
161â†’    onClose();
162â†’  };
163â†’
164â†’  const handleMove = () => {
165â†’    onMove?.();
166â†’    onClose();
167â†’  };
168â†’
169â†’  const handleDelete = () => {
170â†’    onDelete();
171â†’    onClose();
172â†’  };
173â†’
174â†’  const handleDetails = () => {
175â†’    onDetails();
176â†’    onClose();
177â†’  };
178â†’
179â†’  const handleBatchDownload = () => {
180â†’    onBatchDownload?.();
181â†’    onClose();
182â†’  };
183â†’
184â†’  const handleBatchMove = () => {
185â†’    onBatchMove?.();
186â†’    onClose();
187â†’  };
188â†’
189â†’  const handleBatchDelete = () => {
190â†’    onBatchDelete?.();
191â†’    onClose();
192â†’  };
193â†’
194â†’  // Combine refs for menu
195â†’  const setMenuRef = (node: HTMLDivElement | null) => {
196â†’    menuRef.current = node;
197â†’    refs.setFloating(node);
198â†’  };
199â†’
200â†’  // Multi-selection mode: show batch actions
201â†’  const isMultiSelect = selectedCount > 1;
202â†’
203â†’  return (
204â†’    <Portal>
205â†’      <div
206â†’        ref={setMenuRef}
207â†’        className="context-menu"
208â†’        style={floatingStyles}
209â†’        role="menu"
210â†’        aria-label={
211â†’          isMultiSelect ? `Actions for ${selectedCount} items` : `Actions for ${item.name}`
212â†’        }
213â†’      >
214â†’        {isMultiSelect ? (
215â†’          <>
216â†’            {/* Multi-selection header */}
217â†’            <div className="context-menu-header">{selectedCount} items selected</div>
218â†’            <div className="context-menu-divider" role="separator" />
219â†’
220â†’            {/* Batch Download */}
221â†’            {onBatchDownload && (
222â†’              <button
223â†’                type="button"
224â†’                className="context-menu-item"
225â†’                onClick={handleBatchDownload}
226â†’                role="menuitem"
227â†’              >
228â†’                <span className="context-menu-item-icon">&#8595;</span>
229â†’                Download files
230â†’              </button>
231â†’            )}
232â†’
233â†’            {/* Batch Move */}
234â†’            {onBatchMove && (
235â†’              <button
236â†’                type="button"
237â†’                className="context-menu-item"
238â†’                onClick={handleBatchMove}
239â†’                role="menuitem"
240â†’              >
241â†’                <span className="context-menu-item-icon">&#8594;</span>
242â†’                Move to...
243â†’              </button>
244â†’            )}
245â†’
246â†’            {/* Divider before destructive action */}
247â†’            <div className="context-menu-divider" role="separator" />
248â†’
249â†’            {/* Batch Delete */}
250â†’            {onBatchDelete && (
251â†’              <button
252â†’                type="button"
253â†’                className="context-menu-item context-menu-item--destructive"
254â†’                onClick={handleBatchDelete}
255â†’                role="menuitem"
256â†’              >
257â†’                <span className="context-menu-item-icon">&#128465;</span>
258â†’                Delete {selectedCount} items
259â†’              </button>
260â†’            )}
261â†’          </>
262â†’        ) : (
263â†’          <>
264â†’            {/* Download - files only */}
265â†’            {isFile && onDownload && (
266â†’              <button
267â†’                type="button"
268â†’                className="context-menu-item"
269â†’                onClick={handleDownload}
270â†’                role="menuitem"
271â†’              >
272â†’                <span className="context-menu-item-icon">&#8595;</span>
273â†’                Download
274â†’              </button>
275â†’            )}
276â†’
277â†’            {/* Edit - text files only */}
278â†’            {isFile && onEdit && (
279â†’              <button
280â†’                type="button"
281â†’                className="context-menu-item"
282â†’                onClick={handleEdit}
283â†’                role="menuitem"
284â†’              >
285â†’                <span className="context-menu-item-icon">&#9998;</span>
286â†’                Edit
287â†’              </button>
288â†’            )}
289â†’
290â†’            {/* Preview - image files only */}
291â†’            {isFile && onPreview && (
292â†’              <button
293â†’                type="button"
294â†’                className="context-menu-item"
295â†’                onClick={handlePreview}
296â†’                role="menuitem"
297â†’              >
298â†’                <span className="context-menu-item-icon">&#128444;</span>
299â†’                Preview
300â†’              </button>
301â†’            )}
302â†’
303â†’            {/* Rename */}
304â†’            <button
305â†’              type="button"
306â†’              className="context-menu-item"
307â†’              onClick={handleRename}
308â†’              role="menuitem"
309â†’            >
310â†’              <span className="context-menu-item-icon">&#9998;</span>
311â†’              Rename
312â†’            </button>
313â†’
314â†’            {/* Move to... */}
315â†’            {onMove && (
316â†’              <button
317â†’                type="button"
318â†’                className="context-menu-item"
319â†’                onClick={handleMove}
320â†’                role="menuitem"
321â†’              >
322â†’                <span className="context-menu-item-icon">&#8594;</span>
323â†’                Move to...
324â†’              </button>
325â†’            )}
326â†’
327â†’            {/* Details */}
328â†’            <button
329â†’              type="button"
330â†’              className="context-menu-item"
331â†’              onClick={handleDetails}
332â†’              role="menuitem"
333â†’            >
334â†’              <span className="context-menu-item-icon">&#9432;</span>
335â†’              Details
336â†’            </button>
337â†’
338â†’            {/* Divider before destructive action */}
339â†’            <div className="context-menu-divider" role="separator" />
340â†’
341â†’            {/* Delete */}
342â†’            <button
343â†’              type="button"
344â†’              className="context-menu-item context-menu-item--destructive"
345â†’              onClick={handleDelete}
346â†’              role="menuitem"
347â†’            >
348â†’              <span className="context-menu-item-icon">&#128465;</span>
349â†’              Delete
350â†’            </button>
351â†’          </>
352â†’        )}
353â†’      </div>
354â†’    </Portal>
355â†’  );
356â†’}
