  4â†’/**
  5â†’ * A folder node in the in-memory folder tree.
  6â†’ * Contains decrypted folder data and navigation state.
  7â†’ */
  8â†’export type FolderNode = {
  9â†’  /** UUID for internal reference (null string 'root' for root folder) */
 10â†’  id: string;
 11â†’  /** Folder name */
 12â†’  name: string;
 13â†’  /** IPNS name for this folder (k51.../bafzaa... format) */
