 29â†’};
 30â†’
 31â†’/**
 32â†’ * Breadcrumb entry for navigation.
 33â†’ */
 34â†’type Breadcrumb = {
 35â†’  id: string;
 36â†’  name: string;
 37â†’};
 38â†’
 39â†’type FolderState = {
 40â†’  // Folder tree indexed by id
 41â†’  folders: Record<string, FolderNode>;
 42â†’
 43â†’  // Current navigation
 44â†’  currentFolderId: string | null; // null = root
 45â†’  breadcrumbs: Breadcrumb[];
 46â†’
 47â†’  // Publishing state
 48â†’  pendingPublishes: Set<string>; // folder IDs with pending IPNS publishes
 49â†’
 50â†’  // Actions
 51â†’  setFolder: (folder: FolderNode) => void;
 52â†’  updateFolderChildren: (folderId: string, children: FolderChild[]) => void;
 53â†’  updateFolderSequence: (folderId: string, sequenceNumber: bigint) => void;
 54â†’  setCurrentFolder: (folderId: string | null) => void;
 55â†’  setBreadcrumbs: (breadcrumbs: Breadcrumb[]) => void;
 56â†’  addPendingPublish: (folderId: string) => void;
 57â†’  removePendingPublish: (folderId: string) => void;
 58â†’  removeFolder: (folderId: string) => void;
 59â†’  updateFolderName: (folderId: string, newName: string) => void;
 60â†’  clearFolders: () => void;
 61â†’};
