 63â†’/**
 64â†’ * Folder store for managing folder tree state.
 65â†’ *
 66â†’ * Maintains the in-memory representation of the folder hierarchy
 67â†’ * with decrypted folder keys and metadata.
 68â†’ *
 69â†’ * SECURITY: All folder keys are memory-only - never persisted to storage.
 70â†’ * Call clearFolders() on logout to zero-fill all key material.
 71â†’ */
 72â†’export const useFolderStore = create<FolderState>((set, get) => ({
 73â†’  // State
 74â†’  folders: {},
 75â†’  currentFolderId: null,
 76â†’  breadcrumbs: [],
 77â†’  pendingPublishes: new Set<string>(),
 78â†’
 79â†’  // Actions
 80â†’  setFolder: (folder) =>
 81â†’    set((state) => ({
 82â†’      folders: { ...state.folders, [folder.id]: folder },
 83â†’    })),
 84â†’
 85â†’  updateFolderChildren: (folderId, children) =>
 86â†’    set((state) => {
 87â†’      const folder = state.folders[folderId];
 88â†’      if (!folder) return state;
 89â†’
 90â†’      return {
 91â†’        folders: {
 92â†’          ...state.folders,
 93â†’          [folderId]: { ...folder, children, isLoaded: true, isLoading: false },
 94â†’        },
 95â†’      };
 96â†’    }),
 97â†’
 98â†’  updateFolderSequence: (folderId, sequenceNumber) =>
 99â†’    set((state) => {
100â†’      const folder = state.folders[folderId];
101â†’      if (!folder) return state;
102â†’
103â†’      return {
104â†’        folders: {
105â†’          ...state.folders,
106â†’          [folderId]: { ...folder, sequenceNumber },
107â†’        },
108â†’      };
109â†’    }),
110â†’
111â†’  setCurrentFolder: (folderId) => set({ currentFolderId: folderId }),
112â†’
113â†’  setBreadcrumbs: (breadcrumbs) => set({ breadcrumbs }),
114â†’
115â†’  addPendingPublish: (folderId) =>
116â†’    set((state) => {
117â†’      const newPending = new Set(state.pendingPublishes);
118â†’      newPending.add(folderId);
119â†’      return { pendingPublishes: newPending };
120â†’    }),
121â†’
122â†’  removePendingPublish: (folderId) =>
123â†’    set((state) => {
124â†’      const newPending = new Set(state.pendingPublishes);
125â†’      newPending.delete(folderId);
126â†’      return { pendingPublishes: newPending };
127â†’    }),
128â†’
129â†’  // Remove a folder from local state (after deletion)
130â†’  // Also clears the folder's key material from memory
131â†’  removeFolder: (folderId) =>
132â†’    set((state) => {
133â†’      const folder = state.folders[folderId];
134â†’      if (!folder) return state;
135â†’
136â†’      // Zero-fill keys before removing
137â†’      if (folder.folderKey) folder.folderKey.fill(0);
138â†’      if (folder.ipnsPrivateKey) folder.ipnsPrivateKey.fill(0);
139â†’
140â†’      const { [folderId]: _removed, ...remainingFolders } = state.folders;
141â†’      void _removed; // Intentionally destructured to remove from object
142â†’      return { folders: remainingFolders };
143â†’    }),
144â†’
145â†’  // Update a folder's name in local state
146â†’  updateFolderName: (folderId, newName) =>
147â†’    set((state) => {
148â†’      const folder = state.folders[folderId];
149â†’      if (!folder) return state;
150â†’
151â†’      return {
152â†’        folders: {
153â†’          ...state.folders,
154â†’          [folderId]: { ...folder, name: newName },
155â†’        },
156â†’      };
157â†’    }),
158â†’
159â†’  // [SECURITY: MEDIUM-02] Zero-fill sensitive key material before clearing
160â†’  clearFolders: () => {
161â†’    const state = get();
162â†’
163â†’    // Best-effort memory clearing - overwrite all folder keys with zeros
164â†’    for (const folder of Object.values(state.folders)) {
165â†’      if (folder.folderKey) {
166â†’        folder.folderKey.fill(0);
167â†’      }
168â†’      if (folder.ipnsPrivateKey) {
169â†’        folder.ipnsPrivateKey.fill(0);
170â†’      }
171â†’    }
172â†’
173â†’    set({
174â†’      folders: {},
175â†’      currentFolderId: null,
176â†’      breadcrumbs: [],
177â†’      pendingPublishes: new Set<string>(),
178â†’    });
179â†’  },
180â†’}));
181â†’
182â†’// Expose store on window for E2E testing (development only)
183â†’if (typeof window !== 'undefined' && import.meta.env.DEV) {
184â†’  // eslint-disable-next-line @typescript-eslint/no-explicit-any
185â†’  (window as any).__ZUSTAND_FOLDER_STORE__ = useFolderStore;
186â†’}
