  1â†’/**
  2â†’ * Folder Service - Folder CRUD operations with encryption
  3â†’ *
  4â†’ * Handles folder creation, loading, and metadata updates with
  5â†’ * client-side encryption using @cipherbox/crypto.
  6â†’ *
  7â†’ * Folders store slim FilePointer references. File metadata lives in per-file IPNS records.
  8â†’ */
  9â†’
 10â†’import {
 11â†’  generateEd25519Keypair,
 12â†’  deriveIpnsName,
 13â†’  generateRandomBytes,
 14â†’  wrapKey,
 15â†’  bytesToHex,
 16â†’  hexToBytes,
 17â†’  encryptFolderMetadata,
 18â†’  decryptFolderMetadata,
 19â†’  createIpnsRecord,
 20â†’  marshalIpnsRecord,
 21â†’  type FolderMetadata,
 22â†’  type EncryptedFolderMetadata,
 23â†’  type FolderEntry,
 24â†’  type FolderChild,
 25â†’  type FilePointer,
 26â†’} from '@cipherbox/crypto';
 27â†’import { addToIpfs, fetchFromIpfs } from '../lib/api/ipfs';
 28â†’import { createAndPublishIpnsRecord, resolveIpnsRecord } from './ipns.service';
 29â†’import { batchPublishIpnsRecords } from './ipns.service';
 30â†’import { useAuthStore } from '../stores/auth.store';
 31â†’import type { FolderNode } from '../stores/folder.store';
 32â†’import type { FileIpnsRecordPayload } from './file-metadata.service';
 33â†’
 34â†’/** Maximum folder nesting depth per FOLD-03 */
 35â†’const MAX_FOLDER_DEPTH = 20;
 36â†’
 37â†’/** Safe base64 encoding that avoids call stack overflow from spread operator */
 38â†’function uint8ToBase64(bytes: Uint8Array): string {
 39â†’  let binary = '';
 40â†’  for (let i = 0; i < bytes.length; i++) {
 41â†’    binary += String.fromCharCode(bytes[i]);
 42â†’  }
 43â†’  return btoa(binary);
 44â†’}
 45â†’
 46â†’/**
 47â†’ * Calculate the depth of a folder from root.
 48â†’ * Root folder has depth 0, immediate children have depth 1, etc.
 49â†’ *
 50â†’ * Used for enforcing FOLD-03 depth limit of 20.
 51â†’ *
 52â†’ * @param folderId - Folder ID to calculate depth for (null = root)
 53â†’ * @param folders - Current folder tree
 54â†’ * @returns Depth from root (0 for root)
 55â†’ */
 56â†’export function getDepth(folderId: string | null, folders: Record<string, FolderNode>): number {
 57â†’  if (folderId === null) return 0; // root is depth 0
 58â†’
 59â†’  let depth = 0;
 60â†’  let currentId: string | null = folderId;
 61â†’
 62â†’  while (currentId !== null) {
 63â†’    const folder: FolderNode | undefined = folders[currentId];
 64â†’    if (!folder) break;
 65â†’    depth++;
 66â†’    currentId = folder.parentId;
 67â†’  }
 68â†’
 69â†’  return depth;
 70â†’}
 71â†’
 72â†’/**
 73â†’ * Load a folder's metadata from IPNS.
 74â†’ *
 75â†’ * Resolves the folder's IPNS name to get the current metadata CID,
 76â†’ * fetches and decrypts the metadata, and returns a complete FolderNode.
 77â†’ * Returns v2 metadata with FolderChild[] children.
 78â†’ *
 79â†’ * IMPORTANT: Does NOT eagerly resolve per-file IPNS records during folder load.
 80â†’ * File metadata is lazy-loaded on download/preview (Pitfall 1 from research).
 81â†’ *
 82â†’ * @param folderId - Folder ID (null for root, or UUID)
 83â†’ * @param folderKey - Decrypted AES-256 key for this folder
 84â†’ * @param ipnsPrivateKey - Decrypted Ed25519 private key for IPNS
 85â†’ * @param ipnsName - IPNS name for this folder
 86â†’ * @param parentId - Parent folder ID (null for root)
 87â†’ * @param name - Display name for this folder
 88â†’ */
 89â†’export async function loadFolder(
 90â†’  folderId: string | null,
 91â†’  folderKey: Uint8Array,
 92â†’  ipnsPrivateKey: Uint8Array,
 93â†’  ipnsName: string,
 94â†’  parentId: string | null,
 95â†’  name: string
 96â†’): Promise<FolderNode> {
 97â†’  // 1. Resolve IPNS to get current metadata CID
 98â†’  const resolved = await resolveIpnsRecord(ipnsName);
 99â†’
100â†’  // 2. If IPNS resolution returns null, return unloaded folder so it can be retried
101â†’  //    This handles newly-created subfolders whose IPNS hasn't propagated yet
102â†’  if (!resolved) {
103â†’    console.warn(
104â†’      `IPNS resolution returned null for folder "${name}" (${ipnsName}). Marking as not loaded for retry.`
105â†’    );
106â†’    return {
107â†’      id: folderId ?? 'root',
108â†’      name,
109â†’      ipnsName,
110â†’      parentId,
111â†’      children: [],
112â†’      isLoaded: false,
113â†’      isLoading: false,
114â†’      sequenceNumber: 0n,
115â†’      folderKey,
116â†’      ipnsPrivateKey,
117â†’    };
118â†’  }
119â†’
120â†’  // 3. Fetch encrypted metadata from IPFS and decrypt with folderKey
121â†’  const metadata = await fetchAndDecryptMetadata(resolved.cid, folderKey);
122â†’
123â†’  // 4. Return complete FolderNode with decrypted children
124â†’  // FilePointer objects have fileMetaIpnsName instead of inline file data.
125â†’  return {
126â†’    id: folderId ?? 'root',
127â†’    name,
128â†’    ipnsName,
129â†’    parentId,
130â†’    children: metadata.children ?? [],
131â†’    isLoaded: true,
132â†’    isLoading: false,
133â†’    sequenceNumber: resolved.sequenceNumber,
134â†’    folderKey,
135â†’    ipnsPrivateKey,
136â†’  };
137â†’}
138â†’
139â†’/**
140â†’ * Create a new subfolder with generated keys.
141â†’ *
142â†’ * Generates new Ed25519 IPNS keypair and AES-256 folder key,
143â†’ * wraps them with the user's public key for storage.
144â†’ *
145â†’ * @param params.parentFolderId - Parent folder ID (null for root)
146â†’ * @param params.name - Folder name
147â†’ * @param params.userPublicKey - User's secp256k1 public key for ECIES wrapping
148â†’ * @param params.folders - Current folder tree (for depth checking)
149â†’ * @returns Created folder entry and decrypted keys
150â†’ * @throws Error if depth limit exceeded (FOLD-03)
151â†’ */
152â†’export async function createFolder(params: {
153â†’  parentFolderId: string | null;
154â†’  name: string;
155â†’  userPublicKey: Uint8Array;
156â†’  folders: Record<string, FolderNode>;
157â†’}): Promise<{
158â†’  folder: FolderEntry;
159â†’  ipnsPrivateKey: Uint8Array;
160â†’  folderKey: Uint8Array;
161â†’  encryptedIpnsPrivateKey?: string;
162â†’  keyEpoch?: number;
163â†’}> {
164â†’  // 1. Check depth limit (FOLD-03)
165â†’  const parentDepth = getDepth(params.parentFolderId, params.folders);
166â†’  if (parentDepth >= MAX_FOLDER_DEPTH) {
167â†’    throw new Error(`Cannot create folder: maximum depth of ${MAX_FOLDER_DEPTH} exceeded`);
168â†’  }
169â†’
170â†’  // 2. Generate Ed25519 keypair for folder IPNS
171â†’  const ipnsKeypair = await generateEd25519Keypair();
172â†’  const ipnsName = await deriveIpnsName(ipnsKeypair.publicKey);
173â†’
174â†’  // 3. Generate random AES-256 folder key
175â†’  const folderKey = generateRandomBytes(32);
176â†’
177â†’  // 4. Wrap keys with user's public key (ECIES encryption)
178â†’  // The 64-byte libp2p format private key (privateKey || publicKey) is wrapped
179â†’  const ipnsPrivateKeyEncrypted = bytesToHex(
180â†’    await wrapKey(ipnsKeypair.privateKey, params.userPublicKey)
181â†’  );
182â†’  const folderKeyEncrypted = bytesToHex(await wrapKey(folderKey, params.userPublicKey));
183â†’
184â†’  // 5. TEE-02: Encrypt IPNS private key with TEE public key for republishing
185â†’  let encryptedIpnsPrivateKey: string | undefined;
186â†’  let keyEpoch: number | undefined;
187â†’
188â†’  const teeKeys = useAuthStore.getState().teeKeys;
189â†’  if (teeKeys?.currentPublicKey) {
190â†’    const teePublicKey = hexToBytes(teeKeys.currentPublicKey);
191â†’    const encryptedKey = await wrapKey(ipnsKeypair.privateKey, teePublicKey);
192â†’    encryptedIpnsPrivateKey = bytesToHex(encryptedKey);
193â†’    keyEpoch = teeKeys.currentEpoch;
194â†’  }
195â†’
196â†’  // 6. Create folder entry for parent's metadata
197â†’  const now = Date.now();
198â†’  const folder: FolderEntry = {
199â†’    type: 'folder',
200â†’    id: crypto.randomUUID(),
201â†’    name: params.name,
202â†’    ipnsName,
203â†’    ipnsPrivateKeyEncrypted,
204â†’    folderKeyEncrypted,
205â†’    createdAt: now,
206â†’    modifiedAt: now,
207â†’  };
208â†’
209â†’  return {
210â†’    folder,
211â†’    ipnsPrivateKey: ipnsKeypair.privateKey,
212â†’    folderKey,
213â†’    encryptedIpnsPrivateKey,
214â†’    keyEpoch,
215â†’  };
216â†’}
217â†’
218â†’/**
219â†’ * Update folder metadata and publish to IPNS (v2 format).
220â†’ *
221â†’ * Encrypts the metadata with the folder key, uploads to IPFS,
222â†’ * and publishes an updated IPNS record pointing to the new CID.
223â†’ *
224â†’ * @param params.folderId - Folder being updated
225â†’ * @param params.children - New children array (FolderChild[])
226â†’ * @param params.folderKey - Decrypted AES-256 folder key
227â†’ * @param params.ipnsPrivateKey - Decrypted Ed25519 IPNS private key
228â†’ * @param params.ipnsName - IPNS name for this folder
229â†’ * @param params.sequenceNumber - Current sequence number
230â†’ * @param params.encryptedIpnsPrivateKey - TEE-wrapped key (first publish only)
231â†’ * @param params.keyEpoch - TEE key epoch (required with encryptedIpnsPrivateKey)
232â†’ * @returns New CID and sequence number
233â†’ */
234â†’export async function updateFolderMetadata(params: {
235â†’  folderId: string;
236â†’  children: FolderChild[];
237â†’  folderKey: Uint8Array;
238â†’  ipnsPrivateKey: Uint8Array;
239â†’  ipnsName: string;
240â†’  sequenceNumber: bigint;
241â†’  encryptedIpnsPrivateKey?: string;
242â†’  keyEpoch?: number;
243â†’}): Promise<{ cid: string; newSequenceNumber: bigint }> {
244â†’  // 1. Create v2 folder metadata
245â†’  const metadata: FolderMetadata = {
246â†’    version: 'v2',
247â†’    children: params.children,
248â†’  };
249â†’
250â†’  // 2. Encrypt metadata with folder key
251â†’  const encrypted = await encryptFolderMetadata(metadata, params.folderKey);
252â†’
253â†’  // 3. Upload to IPFS via backend relay
254â†’  const blob = new Blob([JSON.stringify(encrypted)], {
255â†’    type: 'application/json',
256â†’  });
257â†’  const { cid } = await addToIpfs(blob);
258â†’
259â†’  // 4. Publish IPNS record pointing to new CID
260â†’  const newSeq = params.sequenceNumber + 1n;
261â†’  await createAndPublishIpnsRecord({
262â†’    ipnsPrivateKey: params.ipnsPrivateKey,
263â†’    ipnsName: params.ipnsName,
264â†’    metadataCid: cid,
265â†’    sequenceNumber: newSeq,
266â†’    encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
267â†’    keyEpoch: params.keyEpoch,
268â†’  });
269â†’
270â†’  return { cid, newSequenceNumber: newSeq };
271â†’}
