273â†’/**
274â†’ * Build a folder IPNS record payload for batch publishing.
275â†’ *
276â†’ * Creates and signs an IPNS record locally without publishing.
277â†’ * Returns the payload that can be included in a batch publish call.
278â†’ *
279â†’ * @returns Folder IPNS record payload for batch publish
280â†’ */
281â†’async function buildFolderIpnsRecord(params: {
282â†’  children: FolderChild[];
283â†’  folderKey: Uint8Array;
284â†’  ipnsPrivateKey: Uint8Array;
285â†’  ipnsName: string;
286â†’  sequenceNumber: bigint;
287â†’  encryptedIpnsPrivateKey?: string;
288â†’  keyEpoch?: number;
289â†’}): Promise<{
290â†’  cid: string;
291â†’  record: FileIpnsRecordPayload & { recordType: 'folder' };
292â†’  newSequenceNumber: bigint;
293â†’}> {
294â†’  // 1. Create v2 folder metadata
295â†’  const metadata: FolderMetadata = {
296â†’    version: 'v2',
297â†’    children: params.children,
298â†’  };
299â†’
300â†’  // 2. Encrypt metadata with folder key
301â†’  const encrypted = await encryptFolderMetadata(metadata, params.folderKey);
302â†’
303â†’  // 3. Upload to IPFS
304â†’  const blob = new Blob([JSON.stringify(encrypted)], { type: 'application/json' });
305â†’  const { cid } = await addToIpfs(blob);
306â†’
307â†’  // 4. Create IPNS record
308â†’  const newSeq = params.sequenceNumber + 1n;
309â†’  const record = await createIpnsRecord(
310â†’    params.ipnsPrivateKey,
311â†’    `/ipfs/${cid}`,
312â†’    newSeq,
313â†’    24 * 60 * 60 * 1000
314â†’  );
315â†’  const recordBytes = marshalIpnsRecord(record);
316â†’  const recordBase64 = uint8ToBase64(recordBytes);
317â†’
318â†’  return {
319â†’    cid,
320â†’    record: {
321â†’      ipnsName: params.ipnsName,
322â†’      recordBase64,
323â†’      metadataCid: cid,
324â†’      encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
325â†’      keyEpoch: params.keyEpoch,
326â†’      recordType: 'folder',
327â†’    },
328â†’    newSequenceNumber: newSeq,
329â†’  };
330â†’}
