332â†’/**
333â†’ * Rename a folder within its parent.
334â†’ *
335â†’ * Updates the folder entry's name in the parent's metadata and publishes
336â†’ * an updated IPNS record for the parent folder.
337â†’ *
338â†’ * @param params.folderId - ID of folder to rename
339â†’ * @param params.newName - New name for the folder
340â†’ * @param params.parentFolderState - Parent folder containing this folder
341â†’ * @throws Error if folder not found or name collision exists
342â†’ */
343â†’export async function renameFolder(params: {
344â†’  folderId: string;
345â†’  newName: string;
346â†’  parentFolderState: FolderNode;
347â†’}): Promise<void> {
348â†’  // 1. Find folder entry in parent's children
349â†’  const children = [...params.parentFolderState.children];
350â†’  const folderIndex = children.findIndex((c) => c.type === 'folder' && c.id === params.folderId);
351â†’
352â†’  if (folderIndex === -1) throw new Error('Folder not found');
353â†’
354â†’  // 2. Check for name collision
355â†’  const nameExists = children.some((c) => c.name === params.newName && c.id !== params.folderId);
356â†’  if (nameExists) throw new Error('An item with this name already exists');
357â†’
358â†’  // 3. Update name and modifiedAt
359â†’  const folder = children[folderIndex] as FolderEntry;
360â†’  children[folderIndex] = {
361â†’    ...folder,
362â†’    name: params.newName,
363â†’    modifiedAt: Date.now(),
364â†’  };
365â†’
366â†’  // 4. Update parent folder metadata and publish
367â†’  await updateFolderMetadata({
368â†’    folderId: params.parentFolderState.id,
369â†’    children,
370â†’    folderKey: params.parentFolderState.folderKey,
371â†’    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
372â†’    ipnsName: params.parentFolderState.ipnsName,
373â†’    sequenceNumber: params.parentFolderState.sequenceNumber,
374â†’  });
375â†’}
