377â†’/**
378â†’ * Delete a folder and all its contents recursively.
379â†’ *
380â†’ * For v2 metadata: collects fileMetaIpnsName values from FilePointer children
381â†’ * (not CIDs, since CIDs live in per-file IPNS records, not folder metadata).
382â†’ * Removes the folder from the parent's metadata, publishes the update.
383â†’ * Returns the collected file IPNS names so the caller can resolve them for
384â†’ * CID unpinning and TEE unenrollment.
385â†’ *
386â†’ * @param params.folderId - ID of folder to delete
387â†’ * @param params.parentFolderState - Parent folder containing this folder
388â†’ * @param params.getFolderState - Function to get folder state by ID (for recursion)
389â†’ * @param params.unpinCid - Function to unpin a CID from IPFS
390â†’ * @throws Error if folder not found
391â†’ */
392â†’export async function deleteFolder(params: {
393â†’  folderId: string;
394â†’  parentFolderState: FolderNode;
395â†’  getFolderState: (id: string) => FolderNode | undefined;
396â†’  unpinCid: (cid: string) => Promise<void>;
397â†’}): Promise<string[]> {
398â†’  // 1. Find folder in parent's children
399â†’  const children = [...params.parentFolderState.children];
400â†’  const folderIndex = children.findIndex((c) => c.type === 'folder' && c.id === params.folderId);
401â†’
402â†’  if (folderIndex === -1) throw new Error('Folder not found');
403â†’
404â†’  // 2. Recursively collect file IPNS names for cleanup
405â†’  // In v2, file children are FilePointers with fileMetaIpnsName (no inline CID).
406â†’  // The caller must resolve each fileMetaIpnsName to get the CID for unpinning.
407â†’  const fileIpnsNames: string[] = [];
408â†’  const collectFileIpnsNames = (folderId: string) => {
409â†’    const folder = params.getFolderState(folderId);
410â†’    if (!folder) return;
411â†’
412â†’    for (const child of folder.children) {
413â†’      if (child.type === 'file') {
414â†’        const filePointer = child as FilePointer;
415â†’        if (filePointer.fileMetaIpnsName) {
416â†’          fileIpnsNames.push(filePointer.fileMetaIpnsName);
417â†’        }
418â†’      } else if (child.type === 'folder') {
419â†’        collectFileIpnsNames(child.id);
420â†’      }
421â†’    }
422â†’  };
423â†’
424â†’  collectFileIpnsNames(params.folderId);
425â†’
426â†’  // 3. Remove folder from parent's children
427â†’  children.splice(folderIndex, 1);
428â†’
429â†’  // 4. Update parent folder metadata and publish
430â†’  await updateFolderMetadata({
431â†’    folderId: params.parentFolderState.id,
432â†’    children,
433â†’    folderKey: params.parentFolderState.folderKey,
434â†’    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
435â†’    ipnsName: params.parentFolderState.ipnsName,
436â†’    sequenceNumber: params.parentFolderState.sequenceNumber,
437â†’  });
438â†’
439â†’  // 5. File CID unpinning and TEE unenrollment deferred to caller
440â†’  // The caller (useFolder hook) resolves fileMetaIpnsName -> CID for unpinning.
441â†’  // TODO: Phase 14 should add batch unenrollment for orphaned file IPNS records.
442â†’
443â†’  return fileIpnsNames;
