446â†’/**
447â†’ * Delete a file from its parent folder (v2).
448â†’ *
449â†’ * Removes the FilePointer from the parent's metadata, publishes the update.
450â†’ * Returns the fileMetaIpnsName so the caller can resolve it for CID unpinning
451â†’ * and TEE unenrollment.
452â†’ *
453â†’ * Note: This handles folder metadata update. File CID unpinning is handled
454â†’ * by the caller after resolving the file metadata.
455â†’ *
456â†’ * @param params.fileId - ID of file to delete
457â†’ * @param params.parentFolderState - Parent folder containing this file
458â†’ * @returns fileMetaIpnsName for caller to handle cleanup
459â†’ * @throws Error if file not found
460â†’ */
461â†’export async function deleteFileFromFolder(params: {
462â†’  fileId: string;
463â†’  parentFolderState: FolderNode;
464â†’}): Promise<{ fileMetaIpnsName: string | undefined }> {
465â†’  // 1. Find file in parent's children
466â†’  const children = [...params.parentFolderState.children];
467â†’  const fileIndex = children.findIndex((c) => c.type === 'file' && c.id === params.fileId);
468â†’
469â†’  if (fileIndex === -1) throw new Error('File not found');
470â†’
471â†’  // 2. Get fileMetaIpnsName for cleanup
472â†’  const filePointer = children[fileIndex] as FilePointer;
473â†’  const fileMetaIpnsName = filePointer.fileMetaIpnsName;
474â†’
475â†’  // 3. Remove file from parent's children
476â†’  children.splice(fileIndex, 1);
477â†’
478â†’  // 4. Update parent folder metadata and publish
479â†’  await updateFolderMetadata({
480â†’    folderId: params.parentFolderState.id,
481â†’    children,
482â†’    folderKey: params.parentFolderState.folderKey,
483â†’    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
484â†’    ipnsName: params.parentFolderState.ipnsName,
485â†’    sequenceNumber: params.parentFolderState.sequenceNumber,
486â†’  });
487â†’
488â†’  // 5. TEE unenrollment: no API endpoint available yet.
489â†’  // TODO: Phase 14 should expose unenrollIpns via REST API.
490â†’  if (fileMetaIpnsName) {
491â†’    console.warn(
492â†’      `File IPNS record ${fileMetaIpnsName} orphaned after deletion. TEE unenrollment deferred to Phase 14.`
493â†’    );
494â†’  }
495â†’
496â†’  return { fileMetaIpnsName };
497â†’}
