499â†’/**
500â†’ * Add a file to a folder after successful upload (v2).
501â†’ *
502â†’ * Creates a FilePointer from the file's IPNS record and adds it to the folder's
503â†’ * children array. Publishes both the file IPNS record and the updated folder
504â†’ * metadata via a single batch API call.
505â†’ *
506â†’ * @param params.parentFolderState - Parent folder to add file to
507â†’ * @param params.fileId - Pre-generated file UUID
508â†’ * @param params.name - Original file name
509â†’ * @param params.fileIpnsRecord - File IPNS record payload from createFileMetadata
510â†’ * @returns Created file pointer and new sequence number
511â†’ * @throws Error if name collision exists
512â†’ */
513â†’export async function addFileToFolder(params: {
514â†’  parentFolderState: FolderNode;
515â†’  fileId: string;
516â†’  name: string;
517â†’  fileIpnsRecord: FileIpnsRecordPayload;
518â†’  ipnsPrivateKeyEncrypted: string;
519â†’}): Promise<{ filePointer: FilePointer; newSequenceNumber: bigint }> {
520â†’  // 1. Check for name collision
521â†’  const nameExists = params.parentFolderState.children.some((c) => c.name === params.name);
522â†’  if (nameExists) {
523â†’    throw new Error('A file with this name already exists');
524â†’  }
525â†’
526â†’  // 2. Create FilePointer (slim reference to per-file IPNS record + wrapped key)
527â†’  const now = Date.now();
528â†’  const filePointer: FilePointer = {
529â†’    type: 'file',
530â†’    id: params.fileId,
531â†’    name: params.name,
532â†’    fileMetaIpnsName: params.fileIpnsRecord.ipnsName,
533â†’    ipnsPrivateKeyEncrypted: params.ipnsPrivateKeyEncrypted,
534â†’    createdAt: now,
535â†’    modifiedAt: now,
536â†’  };
537â†’
538â†’  // 3. Add FilePointer to parent's children
539â†’  const children: FolderChild[] = [...params.parentFolderState.children, filePointer];
540â†’
541â†’  // 4. Build folder IPNS record for batch publish
542â†’  const folderResult = await buildFolderIpnsRecord({
543â†’    children,
544â†’    folderKey: params.parentFolderState.folderKey,
545â†’    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
546â†’    ipnsName: params.parentFolderState.ipnsName,
547â†’    sequenceNumber: params.parentFolderState.sequenceNumber,
548â†’  });
549â†’
550â†’  // 5. Batch publish: file IPNS record + folder IPNS record
551â†’  const publishResult = await batchPublishIpnsRecords([
552â†’    { ...params.fileIpnsRecord, recordType: 'file' },
553â†’    folderResult.record,
554â†’  ]);
555â†’
556â†’  if (publishResult.totalFailed > 0) {
557â†’    console.error(
558â†’      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
559â†’    );
560â†’    throw new Error('Failed to publish one or more IPNS records');
561â†’  }
562â†’
563â†’  return { filePointer, newSequenceNumber: folderResult.newSequenceNumber };
564â†’}
