566â†’/**
567â†’ * Add multiple files to a folder after successful upload (v2 batch).
568â†’ *
569â†’ * Creates FilePointer objects for all files, checks for name collisions,
570â†’ * then publishes all N file IPNS records + 1 folder IPNS record via a
571â†’ * single batch API call.
572â†’ *
573â†’ * @param params.parentFolderState - Parent folder to add files to
574â†’ * @param params.files - Array of file data with pre-created IPNS records
575â†’ * @returns Created file pointers and new sequence number
576â†’ * @throws Error if any name collision exists
577â†’ */
578â†’export async function addFilesToFolder(params: {
579â†’  parentFolderState: FolderNode;
580â†’  files: Array<{
581â†’    fileId: string;
582â†’    name: string;
583â†’    fileIpnsRecord: FileIpnsRecordPayload;
584â†’    ipnsPrivateKeyEncrypted: string;
585â†’  }>;
586â†’}): Promise<{ filePointers: FilePointer[]; newSequenceNumber: bigint }> {
587â†’  // 1. Build a set of existing child names for collision detection
588â†’  const existingNames = new Set(params.parentFolderState.children.map((c) => c.name));
589â†’
590â†’  // 2. Create FilePointer for each file, checking collisions
591â†’  const filePointers: FilePointer[] = [];
592â†’  const now = Date.now();
593â†’
594â†’  for (const file of params.files) {
595â†’    if (existingNames.has(file.name)) {
596â†’      throw new Error(`A file with name "${file.name}" already exists`);
597â†’    }
598â†’    existingNames.add(file.name);
599â†’
600â†’    const filePointer: FilePointer = {
601â†’      type: 'file',
602â†’      id: file.fileId,
603â†’      name: file.name,
604â†’      fileMetaIpnsName: file.fileIpnsRecord.ipnsName,
605â†’      ipnsPrivateKeyEncrypted: file.ipnsPrivateKeyEncrypted,
606â†’      createdAt: now,
607â†’      modifiedAt: now,
608â†’    };
609â†’    filePointers.push(filePointer);
610â†’  }
611â†’
612â†’  // 3. Build updated children array
613â†’  const children: FolderChild[] = [...params.parentFolderState.children, ...filePointers];
614â†’
615â†’  // 4. Build folder IPNS record
616â†’  const folderResult = await buildFolderIpnsRecord({
617â†’    children,
618â†’    folderKey: params.parentFolderState.folderKey,
619â†’    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
620â†’    ipnsName: params.parentFolderState.ipnsName,
621â†’    sequenceNumber: params.parentFolderState.sequenceNumber,
622â†’  });
623â†’
624â†’  // 5. Batch publish: all N file IPNS records + 1 folder IPNS record (single API call)
625â†’  const allRecords = [
626â†’    ...params.files.map((f) => ({ ...f.fileIpnsRecord, recordType: 'file' as const })),
627â†’    folderResult.record,
628â†’  ];
629â†’  const publishResult = await batchPublishIpnsRecords(allRecords);
630â†’
631â†’  if (publishResult.totalFailed > 0) {
632â†’    console.error(
633â†’      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
634â†’    );
635â†’    throw new Error('Failed to publish one or more IPNS records');
636â†’  }
637â†’
638â†’  return { filePointers, newSequenceNumber: folderResult.newSequenceNumber };
639â†’}
