678â†’/**
679â†’ * Calculate the maximum depth of a folder's subtree.
680â†’ *
681â†’ * Used to check if moving a folder would exceed the depth limit.
682â†’ *
683â†’ * @param folderId - Folder ID to calculate subtree depth for
684â†’ * @param folders - Current folder tree
685â†’ * @returns Maximum depth in the subtree (0 if folder has no children)
686â†’ */
687â†’export function calculateSubtreeDepth(
688â†’  folderId: string,
689â†’  folders: Record<string, FolderNode>
690â†’): number {
691â†’  const folder = folders[folderId];
692â†’  if (!folder) return 0;
693â†’
694â†’  let maxChildDepth = 0;
695â†’  for (const child of folder.children) {
696â†’    if (child.type === 'folder') {
697â†’      const childDepth = 1 + calculateSubtreeDepth(child.id, folders);
698â†’      maxChildDepth = Math.max(maxChildDepth, childDepth);
699â†’    }
700â†’  }
701â†’
702â†’  return maxChildDepth;
703â†’}
