705â†’/**
706â†’ * Check if a folder is a descendant of a potential ancestor.
707â†’ *
708â†’ * Used to prevent moving a folder into itself or its descendants.
709â†’ *
710â†’ * @param folderId - Folder to check
711â†’ * @param potentialAncestorId - Potential ancestor folder ID
712â†’ * @param folders - Current folder tree
713â†’ * @returns true if folderId is a descendant of potentialAncestorId
714â†’ */
715â†’export function isDescendantOf(
716â†’  folderId: string,
717â†’  potentialAncestorId: string,
718â†’  folders: Record<string, FolderNode>
719â†’): boolean {
720â†’  let currentId: string | null = folderId;
721â†’
722â†’  while (currentId !== null) {
723â†’    if (currentId === potentialAncestorId) return true;
724â†’    const currentFolder: FolderNode | undefined = folders[currentId];
725â†’    if (!currentFolder) break;
726â†’    currentId = currentFolder.parentId;
727â†’  }
728â†’
729â†’  return false;
730â†’}
