732â†’/**
733â†’ * Move a folder from one parent to another.
734â†’ *
735â†’ * Uses add-before-remove pattern to prevent data loss on failure.
736â†’ * Validates depth limit and prevents moving folder into itself.
737â†’ *
738â†’ * @param params.folderId - ID of folder to move
739â†’ * @param params.sourceFolderState - Current parent folder
740â†’ * @param params.destFolderState - Destination parent folder
741â†’ * @param params.folders - Full folder tree (for depth calculation)
742â†’ * @throws Error if folder not found, name collision, depth limit exceeded,
743â†’ *         or attempting to move folder into itself/descendant
744â†’ */
745â†’export async function moveFolder(params: {
746â†’  folderId: string;
747â†’  sourceFolderState: FolderNode;
748â†’  destFolderState: FolderNode;
749â†’  folders: Record<string, FolderNode>;
750â†’}): Promise<void> {
751â†’  // 1. Find folder in source
752â†’  const folder = params.sourceFolderState.children.find(
753â†’    (c) => c.type === 'folder' && c.id === params.folderId
754â†’  ) as FolderEntry | undefined;
755â†’
756â†’  if (!folder) throw new Error('Folder not found');
757â†’
758â†’  // 2. Check name collision in destination
759â†’  const nameExists = params.destFolderState.children.some((c) => c.name === folder.name);
760â†’  if (nameExists) throw new Error('An item with this name already exists in destination');
761â†’
762â†’  // 3. Check depth limit (20 levels max)
763â†’  const destDepth = getDepth(params.destFolderState.id, params.folders);
764â†’  const folderSubtreeDepth = calculateSubtreeDepth(params.folderId, params.folders);
765â†’  if (destDepth + 1 + folderSubtreeDepth > MAX_FOLDER_DEPTH) {
766â†’    throw new Error(`Cannot move: would exceed maximum folder depth of ${MAX_FOLDER_DEPTH}`);
767â†’  }
768â†’
769â†’  // 4. Prevent moving folder into itself or its descendants
770â†’  if (isDescendantOf(params.destFolderState.id, params.folderId, params.folders)) {
771â†’    throw new Error('Cannot move folder into itself or its subfolder');
772â†’  }
773â†’
774â†’  // 5. ADD to destination FIRST (add-before-remove pattern)
775â†’  const destChildren: FolderChild[] = [
776â†’    ...params.destFolderState.children,
777â†’    {
778â†’      ...folder,
779â†’      modifiedAt: Date.now(),
780â†’    },
781â†’  ];
782â†’
783â†’  await updateFolderMetadata({
784â†’    folderId: params.destFolderState.id,
785â†’    children: destChildren,
786â†’    folderKey: params.destFolderState.folderKey,
787â†’    ipnsPrivateKey: params.destFolderState.ipnsPrivateKey,
788â†’    ipnsName: params.destFolderState.ipnsName,
789â†’    sequenceNumber: params.destFolderState.sequenceNumber,
790â†’  });
791â†’
792â†’  // 6. REMOVE from source AFTER destination confirmed
793â†’  const sourceChildren = params.sourceFolderState.children.filter(
794â†’    (c) => !(c.type === 'folder' && c.id === params.folderId)
795â†’  );
796â†’
797â†’  await updateFolderMetadata({
798â†’    folderId: params.sourceFolderState.id,
799â†’    children: sourceChildren,
800â†’    folderKey: params.sourceFolderState.folderKey,
801â†’    ipnsPrivateKey: params.sourceFolderState.ipnsPrivateKey,
802â†’    ipnsName: params.sourceFolderState.ipnsName,
803â†’    sequenceNumber: params.sourceFolderState.sequenceNumber,
804â†’  });
805â†’}
