927â†’
928â†’  // 2. Parse as JSON to get EncryptedFolderMetadata (contains iv and data fields)
929â†’  const encryptedJson = new TextDecoder().decode(encryptedBytes);
930â†’  const encrypted: EncryptedFolderMetadata = JSON.parse(encryptedJson);
931â†’
932â†’  // 3. Decrypt using folder key
933â†’  const metadata = await decryptFolderMetadata(encrypted, folderKey);
934â†’
935â†’  return metadata;
