  90â†’
  91â†’/** Well-known extensionless text filenames. */
  92â†’const TEXT_FILENAMES = new Set([
  93â†’  'dockerfile',
  94â†’  'makefile',
  95â†’  'rakefile',
  96â†’  'gemfile',
