 101â†’/**
 102â†’ * Check if a filename has a text-editable extension.
 103â†’ */
 104â†’function isTextFile(name: string): boolean {
 105â†’  const lower = name.toLowerCase();
 106â†’  // Handle dotfiles like .gitignore, .editorconfig
 107â†’  if (TEXT_EXTENSIONS.has(lower)) return true;
 108â†’  // Handle well-known extensionless filenames like Dockerfile, Makefile
 109â†’  if (TEXT_FILENAMES.has(lower)) return true;
 110â†’  const lastDot = lower.lastIndexOf('.');
 111â†’  if (lastDot === -1) return false;
 112â†’  return TEXT_EXTENSIONS.has(lower.slice(lastDot));
 113â†’}
 114â†’
 115â†’/** Extensions recognized as previewable image files. */
 116â†’const IMAGE_EXTENSIONS = new Set([
 117â†’  '.png',
 118â†’  '.jpg',
 119â†’  '.jpeg',
 120â†’  '.gif',
 121â†’  '.webp',
 122â†’  '.svg',
 123â†’  '.bmp',
 124â†’  '.ico',
