 160â†’function isAudioFile(name: string): boolean {
 161â†’  const lower = name.toLowerCase();
 162â†’  const lastDot = lower.lastIndexOf('.');
 163â†’  if (lastDot === -1) return false;
 164â†’  return AUDIO_EXTENSIONS.has(lower.slice(lastDot));
