 221â†’export function FileBrowser() {
 222â†’  // Navigation state from hook
 223â†’  const { currentFolderId, currentFolder, breadcrumbs, isLoading, navigateTo, navigateUp } =
 224â†’    useFolderNavigation();
 225â†’
 226â†’  // Folder operations
 227â†’  const {
 228â†’    createFolder,
 229â†’    renameItem,
 230â†’    moveItem,
 231â†’    moveItems,
 232â†’    deleteItem,
 233â†’    deleteItems,
