 236â†’
 237â†’  // File download
 238â†’  const { downloadFromIpns, isDownloading } = useFileDownload();
 239â†’
 240â†’  // External file drop upload
 241â†’  const { handleFileDrop } = useDropUpload();
 242â†’
 243â†’  // Context menu state
 244â†’  const contextMenu = useContextMenu();
 245â†’
 246â†’  // Vault and folder stores for sync
 247â†’  const { rootIpnsName } = useVaultStore();
 248â†’  const initialSyncComplete = useSyncStore((state) => state.initialSyncComplete);
 249â†’  const syncStatus = useSyncStore((state) => state.status);
 250â†’
 251â†’  // Sync callback - compare remote sequence with local, refresh if different
 252â†’  const handleSync = useCallback(async () => {
 253â†’    if (!rootIpnsName) return;
 254â†’
 255â†’    // Resolve root folder IPNS to get remote CID and sequence number
 256â†’    // Note: resolveIpnsRecord returns null when the record definitively
 257â†’    // doesn't exist (backend 404 / success:false). Transient failures
 258â†’    // (network errors, 5xx) throw instead, which useSyncPolling catches.
 259â†’    const resolved = await resolveIpnsRecord(rootIpnsName);
 260â†’    if (!resolved) {
 261â†’      // No IPNS record found. This is expected for vaults that have
 262â†’      // never published (empty vault, no uploads yet). Return normally
 263â†’      // so useSyncPolling marks initial sync complete and shows the
 264â†’      // empty file browser instead of retrying forever.
 265â†’      return;
 266â†’    }
 267â†’
 268â†’    // Read fresh state from store â€” avoid stale closure from render cycle.
 269â†’    // On initial load, the root folder may not be in the closure's `folders`
 270â†’    // yet because useFolderNavigation's useEffect sets it in the same commit.
 271â†’    const rootFolder = useFolderStore.getState().folders['root'];
 272â†’    if (!rootFolder) return;
 273â†’
 274â†’    // Compare sequence numbers - if remote > local, we need to refresh
 275â†’    // Sequence number comparison is more reliable than CID since we
 276â†’    // don't cache the local CID, and sequence always increments
 277â†’    if (resolved.sequenceNumber <= rootFolder.sequenceNumber) {
 278â†’      // No changes, already up to date
 279â†’      return;
 280â†’    }
 281â†’
 282â†’    // Remote has newer version - fetch and decrypt new metadata
 283â†’    try {
 284â†’      const metadata = await fetchAndDecryptMetadata(resolved.cid, rootFolder.folderKey);
 285â†’
 286â†’      // Update folder store with new children
 287â†’      // Per CONTEXT.md: last write wins, instant refresh (no toast/prompt)
 288â†’      useFolderStore.getState().updateFolderChildren('root', metadata.children);
 289â†’      useFolderStore.getState().updateFolderSequence('root', resolved.sequenceNumber);
 290â†’    } catch (err) {
 291â†’      console.error('Sync refresh failed:', err);
 292â†’      // During initial sync, propagate so useSyncPolling keeps the
 293â†’      // loading UI visible and retries instead of showing empty state
 294â†’      if (!useSyncStore.getState().initialSyncComplete) {
 295â†’        throw err;
 296â†’      }
 297â†’    }
 298â†’  }, [rootIpnsName]);
 299â†’
 300â†’  // Start sync polling (30s interval, pauses when backgrounded/offline)
 301â†’  useSyncPolling(handleSync);
 302â†’
 303â†’  // Device registry polling (60s interval, independent from vault sync)
 304â†’  useDeviceRegistrySync();
 305â†’
 306â†’  // External drag state â€” tracks when files from OS are being dragged over
 307â†’  const [isDraggingExternal, setIsDraggingExternal] = useState(false);
 308â†’  const dragCounterRef = useRef(0);
 309â†’
 310â†’  /**
