 321â†’  }, []);
 322â†’
 323â†’  const handleContentDragOver = useCallback((e: DragEvent) => {
 324â†’    if (isExternalFileDrag(e.dataTransfer)) {
 325â†’      e.preventDefault();
 326â†’      e.dataTransfer.dropEffect = 'copy';
 327â†’    }
 328â†’  }, []);
 329â†’
 330â†’  const handleContentDragLeave = useCallback((e: DragEvent) => {
 331â†’    if (isExternalFileDrag(e.dataTransfer)) {
 332â†’      dragCounterRef.current -= 1;
 333â†’      if (dragCounterRef.current <= 0) {
 334â†’        dragCounterRef.current = 0;
 335â†’        setIsDraggingExternal(false);
 336â†’      }
 337â†’    }
 338â†’  }, []);
