 340â†’  // Reset drag state if user abandons drag outside the window (e.g. drops on desktop
 341â†’  // or presses Escape). Without this the counter/overlay can get stuck.
 342â†’  useEffect(() => {
 343â†’    const resetDragState = () => {
 344â†’      dragCounterRef.current = 0;
 345â†’      setIsDraggingExternal(false);
 346â†’    };
 347â†’    const handleWindowDrop = (e: Event) => {
 348â†’      e.preventDefault();
 349â†’      resetDragState();
 350â†’    };
 351â†’    const handleWindowDragLeave = (e: globalThis.DragEvent) => {
 352â†’      // relatedTarget is null when the drag leaves the browser window entirely
 353â†’      if (!e.relatedTarget) {
