 355â†’      }
 356â†’    };
 357â†’    window.addEventListener('dragend', resetDragState);
 358â†’    window.addEventListener('drop', handleWindowDrop);
 359â†’    window.addEventListener('dragleave', handleWindowDragLeave as EventListener);
 360â†’    return () => {
 361â†’      window.removeEventListener('dragend', resetDragState);
 362â†’      window.removeEventListener('drop', handleWindowDrop);
 363â†’      window.removeEventListener('dragleave', handleWindowDragLeave as EventListener);
 364â†’    };
 365â†’  }, []);
